import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IReview";
import typia from "typia";

export async function test_update_review_valid_invalid_and_not_found(connection: api.IConnection): Promise<void> {
    // 1. Create a new review to update
    const reviewInput = {
        user_id: "11111111-1111-1111-1111-111111111111",
        product_id: "22222222-2222-2222-2222-222222222222",
        content: "Initial review content.",
        language_code: "en",
        verified_purchase: true,
    };
    const created: IReview = await api.functional.communicationReview.reviews.post(
        connection,
        reviewInput,
    );
    typia.assert<IReview>(created);
    TestValidator.equals("content matches after creation")(reviewInput.content)(created.content);
    TestValidator.equals("archived should be false on creation")(false)(created.archived);

    // 2. Update review with valid changes
    const validUpdate = {
        content: "Updated review content!",
        language_code: "ko",
        verified_purchase: false,
        archived: true,
    };
    const updated: IReview = await api.functional.communicationReview.reviews.put(
        connection,
        created.id,
        validUpdate,
    );
    typia.assert<IReview>(updated);
    TestValidator.equals("content updated")(validUpdate.content)(updated.content);
    TestValidator.equals("language_code updated")(validUpdate.language_code)(updated.language_code);
    TestValidator.equals("verified_purchase updated")(validUpdate.verified_purchase)(updated.verified_purchase);
    TestValidator.equals("archived updated")(validUpdate.archived)(updated.archived);
    TestValidator.equals("id invariant")(created.id)(updated.id);
    // updated_at must have changed
    TestValidator.predicate("updated_at changes")(() => updated.updated_at !== created.updated_at);

    // 3. Try to update with invalid data (e.g., empty content, wrong lang code type, verified_purchase as string)
    await TestValidator.httpError("rejects update with empty content")(400)(async () => {
        await api.functional.communicationReview.reviews.put(
            connection,
            created.id,
            { content: "" },
        );
    });
    await TestValidator.httpError("rejects lang code as number")(400)(async () => {
        await api.functional.communicationReview.reviews.put(
            connection,
            created.id,
            { language_code: 2025 as any },
        );
    });
    await TestValidator.httpError("rejects verified_purchase as string")(400)(async () => {
        await api.functional.communicationReview.reviews.put(
            connection,
            created.id,
            { verified_purchase: "yes" as any },
        );
    });
    // Try mutating an immutable field (id)
    await TestValidator.httpError("cannot update id field")(400)(async () => {
        await api.functional.communicationReview.reviews.put(
            connection,
            created.id,
            { id: "fake-id" } as any,
        );
    });

    // 4. Update non-existent review (should 404)
    const fakeId = "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa";
    await TestValidator.httpError("not found for invalid id")(404)(async () => {
        await api.functional.communicationReview.reviews.put(
            connection,
            fakeId,
            { content: "irrelevant" },
        );
    });
}

