export async function test_soft_delete_review_and_idempotency_cases(connection: api.IConnection): Promise<void> {
    // 1. Create a review (POST /communicationReview/reviews)
    const reviewInput: IReview.ICreate = {
        user_id: uuidv4(),
        product_id: uuidv4(),
        content: "Test review for soft-delete.",
        language_code: "en",
        verified_purchase: true,
    };
    const created = await api.functional.communicationReview.reviews.post(connection, reviewInput);
    typia.assert<IReview>(created);
    TestValidator.predicate("archived should be false on new review")(() => created.archived === false);

    // 2. Happy Path: Archive (soft delete) the review
    const deleted = await api.functional.communicationReview.reviews.delete(connection, created.id);
    typia.assert<IReview>(deleted);
    TestValidator.equals("archived true after delete")<boolean>(true)(deleted.archived);
    TestValidator.equals("id unchanged after archive")<string>(created.id)(deleted.id);

    // 3. Idempotency: Deleting the same review twice
    const deletedAgain = await api.functional.communicationReview.reviews.delete(connection, created.id);
