    TestValidator.equals("archived still true after 2nd delete")<boolean>(true)(deletedAgain.archived);
    TestValidator.equals("id again unchanged after 2nd archive")<string>(created.id)(deletedAgain.id);

    // Suggestion: allow updated_at to be checked for update, but we just validate type here.

    // 4. Negative Case: delete with random non-existent ID
    const randomId = uuidv4();
    await TestValidator.httpError("delete non-existent review should return 404")(404)(
        () => api.functional.communicationReview.reviews.delete(connection, randomId)
    );
