import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IReviewMedia } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewMedia";
import typia from "typia";

export async function test_api_list_review_media_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // Step 1: Create test review media entries
    const reviewIdA = "00000000-0000-0000-0000-000000000001";
    const reviewIdB = "00000000-0000-0000-0000-000000000002";

    const mediaA = await api.functional.communicationReview.reviewMedia.post(connection, {
        review_id: reviewIdA,
        media_type: "image",
        uri: "https://example.com/imageA1.jpg",
        alt_text: "A test image A1",
        sequence: 1,
    });
    const mediaB = await api.functional.communicationReview.reviewMedia.post(connection, {
        review_id: reviewIdA,
        media_type: "video",
        uri: "https://example.com/videoA2.mp4",
        alt_text: null,
        sequence: 2,
    });
    const mediaC = await api.functional.communicationReview.reviewMedia.post(connection, {
        review_id: reviewIdB,
        media_type: "image",
        uri: "https://example.com/imageB1.jpg",
        alt_text: "A test image B1",
        sequence: 1,
    });

    // Step 2: List all review media entries (no filter)
    const allResult = await api.functional.communicationReview.reviewMedia.patch(connection, {
        // No filters
    });
    typia.assert(allResult);
    TestValidator.predicate("all review media returned")(
        () => allResult.data.some((m) => m.id === mediaA.id && m.review_id === reviewIdA)
    );
    TestValidator.predicate("all review media returned B")(
        () => allResult.data.some((m) => m.id === mediaB.id && m.review_id === reviewIdA)
    );
    TestValidator.predicate("all review media returned C")(
        () => allResult.data.some((m) => m.id === mediaC.id && m.review_id === reviewIdB)
    );

    // Step 3: Filter by review_id (A)
    const filteredA = await api.functional.communicationReview.reviewMedia.patch(connection, { review_id: reviewIdA });
    typia.assert(filteredA);
    TestValidator.predicate("only A's media returned")(
        () => filteredA.data.every((m) => m.review_id === reviewIdA)
    );
    TestValidator.predicate("all A's inserted media included")(
        () => [mediaA.id, mediaB.id].every((id) => filteredA.data.some((m) => m.id === id))
    );
    TestValidator.predicate("C's media not included in A filter")(
        () => filteredA.data.every((m) => m.id !== mediaC.id)
    );

    // Step 4: Filter by media_type ("image")
    const images = await api.functional.communicationReview.reviewMedia.patch(connection, { media_type: "image" });
    typia.assert(images);
    TestValidator.predicate("only image media returned")(
        () => images.data.every((m) => m.media_type === "image")
    );
    TestValidator.predicate("both A1 and B1 image media included")(
        () => [mediaA.id, mediaC.id].every((id) => images.data.some((m) => m.id === id))
    );
    TestValidator.predicate("no video entries included in image filter")(
        () => images.data.every((m) => m.media_type !== "video")
    );

    // Step 5: Combined filter (reviewIdA + media_type "image")
    const filterCombo = await api.functional.communicationReview.reviewMedia.patch(connection, {
        review_id: reviewIdA,
        media_type: "image",
    });
    typia.assert(filterCombo);
    TestValidator.predicate("A's image media only")(
        () => filterCombo.data.length === 1 && filterCombo.data[0].id === mediaA.id
    );

    // Step 6: Filter with no matching entries
    const noMatch = await api.functional.communicationReview.reviewMedia.patch(connection, {
        review_id: "99999999-9999-9999-9999-999999999999",
    });
    typia.assert(noMatch);
    TestValidator.equals("Empty result for no filter match")(
        [] as IReviewMedia[]
    )(noMatch.data);

    // Step 7: Pagination test (limit = 1)
    const paged = await api.functional.communicationReview.reviewMedia.patch(connection, {
        limit: 1,
    });
    typia.assert(paged);
    TestValidator.predicate("pagination meta and page size")(
        () => paged.pagination && paged.pagination.limit === 1 && Array.isArray(paged.data) && paged.data.length === 1
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IDiscountRule } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscountRule";
import { IPageIDiscountRule } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIDiscountRule";
import typia from "typia";

export async function test_api_list_discount_rules_with_filtering_and_pagination(
    connection: api.IConnection,
): Promise<void> {
    // 1. Filter by active status - expect some rules to exist with active=true and false
    const activeTrueReq: Partial<IDiscountRule.IRequest> = {
        active: true,
    };
    const activeTrueRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, activeTrueReq);
    typia.assert<IPageIDiscountRule>(activeTrueRes);
    TestValidator.predicate("active: true, non-empty or empty array")(() => Array.isArray(activeTrueRes.data));
    if (activeTrueRes.data.length > 0)
        TestValidator.predicate("All returned rules are active")(() => activeTrueRes.data.every(rule => rule.active === true));

    const activeFalseReq: Partial<IDiscountRule.IRequest> = {
        active: false,
    };
    const activeFalseRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, activeFalseReq);
    typia.assert<IPageIDiscountRule>(activeFalseRes);
    TestValidator.predicate("active: false, non-empty or empty array")(() => Array.isArray(activeFalseRes.data));
    if (activeFalseRes.data.length > 0)
        TestValidator.predicate("All returned rules are inactive")(() => activeFalseRes.data.every(rule => rule.active === false));

    // 2. Try to filter by rule type (if at least one exists in happy path response)
    const anyRule = activeTrueRes.data[0] ?? activeFalseRes.data[0];
    if (anyRule) {
        const typeReq: Partial<IDiscountRule.IRequest> = {
            type: anyRule.type,
        };
        const typeRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, typeReq);
        typia.assert<IPageIDiscountRule>(typeRes);
        TestValidator.predicate("Returned rules all have requested type")(() => typeRes.data.every(rule => rule.type === anyRule.type));
    }

    // 3. Filter by campaign_id (if any rule has non-null campaign_id)
    const ruleWithCampaign = [
        ...activeTrueRes.data,
        ...activeFalseRes.data,
    ].find(rule => rule.campaign_id);
    if (ruleWithCampaign && ruleWithCampaign.campaign_id) {
        const campaignReq: Partial<IDiscountRule.IRequest> = {
            campaign_id: ruleWithCampaign.campaign_id,
        };
        const campaignRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, campaignReq);
        typia.assert<IPageIDiscountRule>(campaignRes);
        TestValidator.predicate("Returned rules all have requested campaign_id")(() => campaignRes.data.every(rule => rule.campaign_id === ruleWithCampaign.campaign_id));
    }

    // 4. Filter by created_at_range: find a rule to base a tight range on, and a range with no results
    if (anyRule && anyRule.created_at) {
        // Happy path: precise created_at range including a known rule
        const exactRangeReq: Partial<IDiscountRule.IRequest> = {
            created_at_range: [anyRule.created_at, anyRule.created_at],
        };
        const rangeRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, exactRangeReq);
        typia.assert<IPageIDiscountRule>(rangeRes);
        // Some rule(s) should have the timestamp exactly
        TestValidator.predicate("All returned rules within exact created_at range")(() =>
            rangeRes.data.every(rule => rule.created_at === anyRule.created_at)
        );
        // Edge: totally out-of-range
        const impossibleRangeReq: Partial<IDiscountRule.IRequest> = {
            created_at_range: [
                "2000-01-01T00:00:00Z",
                "2000-01-02T00:00:00Z",
            ],
        };
        const emptyRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, impossibleRangeReq);
        typia.assert<IPageIDiscountRule>(emptyRes);
        TestValidator.equals("Data for impossible date range should be empty")<IDiscountRule[]>([])(emptyRes.data);
    }

    // 5. Pagination: page beyond total pages
    const commonReq: Partial<IDiscountRule.IRequest> = { active: true };
    const firstPageRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, commonReq);
    typia.assert<IPageIDiscountRule>(firstPageRes);
    if (firstPageRes.pagination.pages > 0) {
        const outOfBoundsReq: Partial<IDiscountRule.IRequest> = {
            ...commonReq,
            page: firstPageRes.pagination.pages + 1,
        };
        const outOfPageRes = await api.functional.paymentsDiscounts.discountRules.patch(connection, outOfBoundsReq);
        typia.assert<IPageIDiscountRule>(outOfPageRes);
        TestValidator.equals("Out-of-bounds pagination yields empty data")<IDiscountRule[]>([])(outOfPageRes.data);
    }

    // 6. Ensure all pagination metadata fields exist and are valid numbers
    const checkPageMeta = (res: IPageIDiscountRule, label: string) => {
        TestValidator.predicate(`${label}: pagination required fields`)(() =>
            typeof res.pagination.current === "number" &&
            typeof res.pagination.limit === "number" &&
            typeof res.pagination.records === "number" &&
            typeof res.pagination.pages === "number"
        );
    };
    [activeTrueRes, activeFalseRes, firstPageRes].forEach((res, i) => checkPageMeta(res, `pagination meta (${i})`));
}

