import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IDiscountCampaign } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscountCampaign";
import { IPageIDiscountCampaign } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIDiscountCampaign";
import typia from "typia";

export async function test_list_discount_campaigns_with_various_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. No filter, default pagination
  const noFilterReq = {};
  const resultNoFilter = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    noFilterReq,
  );
  typia.assert<IPageIDiscountCampaign>(resultNoFilter);
  TestValidator.predicate("data length match limit or less")(
    () => resultNoFilter.data.length <= resultNoFilter.pagination.limit,
  );
  TestValidator.equals("pagination.current = 1")(1)(resultNoFilter.pagination.current);
  TestValidator.equals("pagination.limit", undefined)(resultNoFilter.pagination.limit);

  // 2. Active filter = true
  const activeTrueReq = { active: true };
  const resultActiveTrue = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    activeTrueReq,
  );
  typia.assert<IPageIDiscountCampaign>(resultActiveTrue);
  resultActiveTrue.data.forEach(camp => {
    TestValidator.equals("active true")(true)(camp.active);
  });

  // 3. Name filter with non-existent name (should return empty array)
  const nameNegativeReq = { name: "__NO_MATCHING_NAME__" };
  const resultNameNegative = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    nameNegativeReq,
  );
  typia.assert<IPageIDiscountCampaign>(resultNameNegative);
  TestValidator.equals("empty array on no-match")([] as IDiscountCampaign[])(resultNameNegative.data);

  // 4. Start date range filter (future, likely empty)
  const resultFutureDate = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    { start_at_range: ["2035-01-01T00:00:00Z", "2035-02-01T00:00:00Z"] },
  );
  typia.assert<IPageIDiscountCampaign>(resultFutureDate);
  TestValidator.equals("empty for future date range")([] as IDiscountCampaign[])(resultFutureDate.data);

  // 5. Pagination: limit 1, then request 2nd page
  const reqPag1 = { limit: 1, current: 1 } as any;
  const reqPag2 = { limit: 1, current: 2 } as any;
  const resultPage1 = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    reqPag1,
  );
  const resultPage2 = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    reqPag2,
  );
  typia.assert<IPageIDiscountCampaign>(resultPage1);
  typia.assert<IPageIDiscountCampaign>(resultPage2);
  TestValidator.equals("limit 1 returned length")(
    Math.min(resultPage1.pagination.limit, resultPage1.pagination.records)
  )(resultPage1.data.length);
  TestValidator.equals("next page logic")(
    resultPage2.pagination.current,
  )(2);
  TestValidator.equals("page 2 limit")(
    resultPage2.pagination.limit,
  )(1);

  // 6. Out-of-range page (much bigger current than pages)
  const reqOOR = { limit: 1, current: 99999 } as any;
  const resultOOR = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    reqOOR,
  );
  typia.assert<IPageIDiscountCampaign>(resultOOR);
  TestValidator.equals("empty on out-of-bounds page")([] as IDiscountCampaign[])(resultOOR.data);

  // 7. All response discount campaigns must have required fields
  [
    resultNoFilter.data,
    resultActiveTrue.data,
    resultPage1.data,
    resultPage2.data,
  ].forEach((arr, idx) => {
    arr.forEach((dc, j) => {
      TestValidator.predicate(`required fields in data[${idx}][${j}]`)(
        () =>
          typeof dc.id === "string" &&
          typeof dc.name === "string" &&
          typeof dc.start_at === "string" &&
          typeof dc.active === "boolean" &&
          typeof dc.created_at === "string"
      );
    });
  });
}

