import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IDiscountCampaign } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscountCampaign";
import typia from "typia";

export async function test_update_discount_campaign_and_check_business_rules(connection: api.IConnection): Promise<void> {
  // Step 1: Create a valid campaign
  const campaignInput: IDiscountCampaign.ICreate = {
    name: `Spring Sale ${Date.now()}`,
    description: "Initial spring promotion.",
    start_at: new Date(Date.now() + 1000 * 60 * 60 * 24).toISOString(), // tomorrow
    end_at: new Date(Date.now() + 1000 * 60 * 60 * 24 * 8).toISOString(), // +8 days
    active: true,
  };
  const created = await api.functional.paymentsDiscounts.discountCampaigns.post(connection, campaignInput);
  typia.assert<IDiscountCampaign>(created);
  TestValidator.equals("created.id")(created.id)(created.id);

  // Step 2a: Full positive update - change description, move end_at, toggle active
  const updateInput: IDiscountCampaign.IUpdate = {
    name: campaignInput.name,
    description: "Extended description.",
    start_at: campaignInput.start_at,
    end_at: new Date(Date.now() + 1000 * 60 * 60 * 24 * 10).toISOString(), // +10 days
    active: false,
  };
  const updated = await api.functional.paymentsDiscounts.discountCampaigns.put(connection, created.id, updateInput);
  typia.assert<IDiscountCampaign>(updated);
  TestValidator.equals("updated.description")(updateInput.description)(updated.description);
  TestValidator.equals("updated.active")(updateInput.active)(updated.active);
  TestValidator.equals("updated.end_at")(updateInput.end_at)(updated.end_at);

  // Step 3: Negative case - attempt to update a campaign in the past (expired)
  const pastUpdateInput: IDiscountCampaign.IUpdate = {
    name: campaignInput.name,
    description: "Attempt past campaign update.",
    start_at: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5).toISOString(), // 5 days ago
    end_at: new Date(Date.now() - 1000 * 60 * 60 * 24 * 1).toISOString(), // yesterday
    active: false,
  };
  await TestValidator.httpError("update of expired campaign should fail")(400, 409)(
    () => api.functional.paymentsDiscounts.discountCampaigns.put(connection, created.id, pastUpdateInput)
  );

  // Step 4a: Create another campaign, valid but same rule for unique constraint test
  const otherCampaignInput: IDiscountCampaign.ICreate = {
    name: `Summer Sale ${Date.now()}`,
    description: "Overlap validation case",
    start_at: new Date(Date.now() + 1000 * 60 * 60 * 24 * 9).toISOString(), // overlaps with updated campaign end_at
    end_at: new Date(Date.now() + 1000 * 60 * 60 * 24 * 15).toISOString(),
    active: true,
  };
  const otherCreated = await api.functional.paymentsDiscounts.discountCampaigns.post(connection, otherCampaignInput);
  typia.assert<IDiscountCampaign>(otherCreated);

  // Step 4b: Attempt to update other campaign to overlap (name conflict or window overlap)
  const overlapUpdate: IDiscountCampaign.IUpdate = {
    name: updateInput.name, // same as original to simulate rule-specific window overlap
    description: "Try overlap",
    start_at: updateInput.start_at,
    end_at: updateInput.end_at,
    active: true,
  };
  await TestValidator.httpError("campaign window overlap should fail")(400, 409)(
    () => api.functional.paymentsDiscounts.discountCampaigns.put(connection, otherCreated.id, overlapUpdate)
  );
}

