import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ITransaction } from "@ORGANIZATION/PROJECT-api/lib/structures/ITransaction";
import { IPageITransaction } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITransaction";

export async function test_api_list_transactions_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // 1. Typical: filter by type and valid date range
    const now = new Date();
    const past = new Date(now.getTime() - 30 * 24 * 3600 * 1000); // 30 days ago
    const normalFilter = {
        type: "purchase",
        created_after: past.toISOString(),
        created_before: now.toISOString(),
        page: 1,
        limit: 5,
    };
    const result1 = await api.functional.paymentsDiscounts.transactions.patch(connection, normalFilter);
    typia.assert<IPageITransaction>(result1);
    TestValidator.predicate("pagination structure exists")(() => !!result1.pagination && typeof result1.pagination.pages === "number");
    for (const tx of result1.data) {
        typia.assert<ITransaction>(tx);
        TestValidator.predicate("transaction required fields exist")(() => !!tx.id && !!tx.user_id && !!tx.type && !!tx.created_at);
    }

    // 2. Edge: page too high (likely empty)
    const pageHigh = {
        page: 1 + (result1.pagination.pages || 1),
        limit: 5
    };
    const result2 = await api.functional.paymentsDiscounts.transactions.patch(connection, pageHigh);
    typia.assert<IPageITransaction>(result2);
    TestValidator.equals("empty data for too-high page")([] as ITransaction[])(result2.data);

    // 3. Edge: extremely high limit (capped or all data returned as allowed)
    const hugeLimit = {
        limit: 99999,
        page: 1
    };
    const result3 = await api.functional.paymentsDiscounts.transactions.patch(connection, hugeLimit);
    typia.assert<IPageITransaction>(result3);
    TestValidator.predicate("array with at most high limit")(() => Array.isArray(result3.data));
    TestValidator.predicate("not exceeding requested limit")(() => result3.data.length <= 99999);

    // 4. Edge: no matching results (future date and random IDs)
    const future = new Date(now.getTime() + 365 * 24 * 3600 * 1000); // 1 year in the future
    const randomUUID = "bca326fd-c0a6-4bcf-83f7-f7c8a76e0aaa";
    const noMatch = {
        created_after: future.toISOString(),
        user_id: randomUUID,
        order_id: randomUUID,
        page: 1,
        limit: 3
    };
    const result4 = await api.functional.paymentsDiscounts.transactions.patch(connection, noMatch);
    typia.assert<IPageITransaction>(result4);
    TestValidator.equals("empty data for no match")([] as ITransaction[])(result4.data);
    // pagination structure still present
    TestValidator.predicate("pagination field must exist")(() => !!result4.pagination);

    // 5. Negative: invalid page number (zero or negative)
    await TestValidator.httpError("invalid page - must fail")(400)(async () => {
        await api.functional.paymentsDiscounts.transactions.patch(connection, { page: 0, limit: 3 });
    });
    await TestValidator.httpError("negative page - must fail")(400)(async () => {
        await api.functional.paymentsDiscounts.transactions.patch(connection, { page: -4, limit: 3 });
    });
    // 6. Negative: superfluous property
    await TestValidator.httpError("rejects extra property")(400)(async () => {
        await api.functional.paymentsDiscounts.transactions.patch(connection, { type: "refund", unknown: "foo" });
    });
    // 7. Negative: field type error (string instead of integer)
    await TestValidator.httpError("type mismatch in limit")(400)(async () => {
        await api.functional.paymentsDiscounts.transactions.patch(connection, { limit: "not-a-number" });
    });
}

