import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IVoucher } from "@ORGANIZATION/PROJECT-api/lib/structures/IVoucher";
import { IPageIVoucher } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIVoucher";
import typia from "typia";

export async function test_api_list_vouchers_with_multiple_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // 1. Retrieve all vouchers (no filters)
    let req = {};
    let result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
    typia.assert<IPageIVoucher>(result);
    TestValidator.predicate("page 1 data is an array")(() => Array.isArray(result.data));
    TestValidator.predicate("pagination info exists")(() => typeof result.pagination === "object");
    // Save for filter scenarios
    const allVouchers: IVoucher[] = result.data;

    // If there are vouchers, test the filters with valid values
    if (allVouchers.length > 0) {
        const sampleVoucher = allVouchers[0];

        // 2. Filter by user_id
        req = { user_id: sampleVoucher.user_id };
        result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
        typia.assert<IPageIVoucher>(result);
        TestValidator.predicate("filtered by user_id")(() => result.data.every(v => v.user_id === sampleVoucher.user_id));

        // 3a. Filter redeemed=true
        req = { redeemed: true };
        result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
        typia.assert<IPageIVoucher>(result);
        TestValidator.predicate("all vouchers redeemed or not")(() => result.data.every(v => v.redeemed_at !== null && v.redeemed_at !== undefined));

        // 3b. Filter redeemed=false
        req = { redeemed: false };
        result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
        typia.assert<IPageIVoucher>(result);
        TestValidator.predicate("all vouchers unredeemed")(() => result.data.every(v => v.redeemed_at === null || v.redeemed_at === undefined));

        // 4. Filter with date range
        if (sampleVoucher.expires_at) {
            req = { expires_after: sampleVoucher.expires_at };
            result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
            typia.assert<IPageIVoucher>(result);
            TestValidator.predicate("all vouchers expire after the given date")(() => result.data.every(v => v.expires_at && v.expires_at > sampleVoucher.expires_at));
        }
        if (sampleVoucher.created_at) {
            req = { expires_before: sampleVoucher.created_at };
            result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
            typia.assert<IPageIVoucher>(result);
            TestValidator.predicate("all vouchers expire before the given date or are null")(() => result.data.every(v => !v.expires_at || v.expires_at < sampleVoucher.created_at));
        }
        // 5. Pagination edge - limit and get second page
        req = { limit: 1, page: 2 };
        result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
        typia.assert<IPageIVoucher>(result);
        TestValidator.equals("limit is one")<number>(1)(result.pagination.limit);
        TestValidator.equals("current page is two")<number>(2)(result.pagination.current);
        TestValidator.predicate("result data size <= 1")(() => result.data.length <= 1);

        // 6. Combined filter
        req = {
            user_id: sampleVoucher.user_id,
            redeemed: false,
            currency: sampleVoucher.currency,
            limit: 2,
            page: 1
        };
        result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
        typia.assert<IPageIVoucher>(result);
        TestValidator.predicate("Combined filtered vouchers match user/currency/unredeemed")(() =>
            result.data.every(v => v.user_id === sampleVoucher.user_id && (v.redeemed_at === null || v.redeemed_at === undefined) && v.currency === sampleVoucher.currency)
        );
    }
    // 7. Edge case - no matches
    req = {
        user_id: "00000000-0000-0000-0000-000000000000",
        code: "nonexistent-code",
        currency: "ZZZ"
    };
    result = await api.functional.paymentsDiscounts.vouchers.patch(connection, req);
    typia.assert<IPageIVoucher>(result);
    TestValidator.equals("empty data array")<IVoucher[]>([])(result.data);
    TestValidator.predicate("pagination still exists")(() => typeof result.pagination === "object");
}

