    // Data for voucher creation
    const newVoucher: IVoucher.ICreate = {
        user_id: "11111111-1111-1111-1111-111111111111", // Test-recipient UUID
        code: `E2E${Date.now()}`,
        balance: 100.0,
        currency: "USD",
        campaign_id: null,
        payment_method_id: null,
        expires_at: null,
    };
