    // 1. Create a voucher successfully with all required fields
    const voucherInput: IVoucher.ICreate = {
        user_id: "11111111-1111-1111-1111-111111111111",
        code: `VCHR-${Math.floor(Math.random() * 1_000_000)}`,
        balance: 50.0,
