import typia from "typia";

import { v4 as uuidv4 } from "uuid";

export async function test_api_update_voucher_metadata_and_handle_state_transitions(
    connection: api.IConnection,
): Promise<void> {
    // 1. Issue a new voucher
    const user_id = uuidv4();
    const campaign_id = null;
    const payment_method_id = null;
    const unique_code = `E2E-TST-${Date.now()}-${Math.floor(Math.random()*1000)}`;
    const balance = 100.0;
    const currency = "USD";
    const expires_at = new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString();

    const createInput: IVoucher.ICreate = {
        user_id,
        code: unique_code,
        balance,
        currency,
        campaign_id,
        payment_method_id,
        expires_at,
    };

    const created: IVoucher = await api.functional.paymentsDiscounts.vouchers.post(connection, createInput);
    typia.assert<IVoucher>(created);
    TestValidator.equals("created user_id")<string>(user_id)(created.user_id);
    TestValidator.equals("created code")<string>(unique_code)(created.code);
    TestValidator.equals("created balance")<number>(balance)(created.balance);
    TestValidator.equals("created currency")<string>(currency)(created.currency);
    TestValidator.equals("created expires_at")<string | null>(expires_at)(created.expires_at);
    TestValidator.equals("created campaign_id")<string | null>(campaign_id)(created.campaign_id);
    TestValidator.equals("created payment_method_id")<string | null>(payment_method_id)(created.payment_method_id);

    // 2. Positive: Update expiry date
    const new_expiry = new Date(Date.now() + 14 * 24 * 60 * 60 * 1000).toISOString();
    const updateInput: IVoucher.IUpdate = {
        expires_at: new_expiry,
    };
    const updated: IVoucher = await api.functional.paymentsDiscounts.vouchers.put(connection, created.id, updateInput);
    typia.assert<IVoucher>(updated);
    TestValidator.equals("update: expires_at")<string | null>(new_expiry)(updated.expires_at);
    TestValidator.equals("update: id unchanged")<string>(created.id)(updated.id);

    // 3. Negative: Invalid voucher id
    const invalid_id = uuidv4();
    await TestValidator.httpError("update invalid id")(404)(
        () => api.functional.paymentsDiscounts.vouchers.put(connection, invalid_id, { expires_at: new_expiry })
    );

    // 4. Negative: Simulate redemption and attempt forbidden update
    // Simulate via update: set balance to 0 (redemption), then try another update
    const redeemUpdate: IVoucher.IUpdate = { balance: 0 };
    const redeemed: IVoucher = await api.functional.paymentsDiscounts.vouchers.put(connection, created.id, redeemUpdate);
    typia.assert<IVoucher>(redeemed);
    TestValidator.equals("update: balance should be 0 after redemption")<number>(0)(redeemed.balance);

    // Now try to update expires_at again after redemption, expect policy error (e.g., 400 or relevant)
    await TestValidator.httpError("update after redemption forbidden")(400, 409, 403)(
        () => api.functional.paymentsDiscounts.vouchers.put(connection, created.id, { expires_at: new_expiry })
    );
}

