import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IRefund";
import { IPageIRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIRefund";
import typia from "typia";

export async function test_api_list_refunds_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Baseline: No filters, default page
  const baseline = await api.functional.paymentsDiscounts.refunds.patch(
    connection,
    {},
  );
  typia.assert<IPageIRefund>(baseline);
  TestValidator.predicate("Baseline: data is array")(Array.isArray(baseline.data));
  if (baseline.data.length > 0) {
    baseline.data.forEach((refund) => {
      typia.assert<IRefund>(refund);
      [
        "id",
        "payment_id",
        "transaction_id",
        "amount",
        "currency",
        "status",
        "requested_at",
      ].forEach((k) =>
        TestValidator.predicate(`Refund evidence field exists: ${k}`)(
          refund[k as keyof IRefund] !== undefined,
        ),
      );
    });
  }

  // 2. Filter: Find by status (if data exists)
  const status = baseline.data[0]?.status;
  if (status) {
    const byStatus = await api.functional.paymentsDiscounts.refunds.patch(
      connection,
      { status },
    );
    typia.assert<IPageIRefund>(byStatus);
    byStatus.data.forEach((refund) => {
      typia.assert<IRefund>(refund);
      TestValidator.equals("Status matches")(status)(refund.status);
    });
  }

  // 3. Filter: By payment_id (if data exists)
  const payment_id = baseline.data[0]?.payment_id;
  if (payment_id) {
    const byPayment = await api.functional.paymentsDiscounts.refunds.patch(
      connection,
      { payment_id },
    );
    typia.assert<IPageIRefund>(byPayment);
    byPayment.data.forEach((refund) => {
      typia.assert<IRefund>(refund);
      TestValidator.equals("payment_id matches")(payment_id)(refund.payment_id);
    });
  }

  // 4. Filter: Date range on requested_at (if data exists)
  const someRequested = baseline.data[0]?.requested_at;
  if (someRequested) {
    const from = someRequested;
    const to = new Date(new Date(from).getTime() + 60 * 1000).toISOString();
    const byDate = await api.functional.paymentsDiscounts.refunds.patch(
      connection,
      { requested_from: from, requested_to: to },
    );
    typia.assert<IPageIRefund>(byDate);
    byDate.data.forEach((refund) => {
      typia.assert<IRefund>(refund);
      TestValidator.predicate("requested_at in range")(
        new Date(refund.requested_at) >= new Date(from) &&
          new Date(refund.requested_at) <= new Date(to),
      );
    });
  }

  // 5. Pagination: Request first two pages (limit 1)
  const paged1 = await api.functional.paymentsDiscounts.refunds.patch(
    connection,
    { limit: 1, page: 1 },
  );
  typia.assert<IPageIRefund>(paged1);
  const paged2 = await api.functional.paymentsDiscounts.refunds.patch(
    connection,
    { limit: 1, page: 2 },
  );
  typia.assert<IPageIRefund>(paged2);
  if (paged1.data.length && paged2.data.length) {
    TestValidator.predicate("No overlap between pages")(
      paged1.data[0].id !== paged2.data[0].id,
    );
  }

  // 6. Edge: Page out of bounds (large page number)
  const outOfBounds = await api.functional.paymentsDiscounts.refunds.patch(
    connection,
    { page: 99999, limit: 1 },
  );
  typia.assert<IPageIRefund>(outOfBounds);
  TestValidator.equals("empty page data")([] as IRefund[])(outOfBounds.data);

  // 7. Edge: Invalid status filter, expect empty
  const badStatus = await api.functional.paymentsDiscounts.refunds.patch(
    connection,
    { status: "totally_bogus_status_zzz" },
  );
  typia.assert<IPageIRefund>(badStatus);
  TestValidator.equals("empty for bogus status")([] as IRefund[])(badStatus.data);
}

