import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IRefund";
import typia from "typia";

export async function test_create_refund_with_metadata_and_idempotency_checks(connection: api.IConnection): Promise<void> {
    // Positive Case: Create refund (valid)
    const payload: IRefund.ICreate = {
        payment_id: "00000000-0000-0000-0000-000000000001",
        transaction_id: "00000000-0000-0000-0000-000000000101",
        order_id: "00000000-0000-0000-0000-000000001111",
        amount: 900.0,
        currency: "USD",
        status: "requested",
        reason: "Customer request: duplicate order"
    };

    const refund = await api.functional.paymentsDiscounts.refunds.post(connection, payload);
    typia.assert<IRefund>(refund);
    TestValidator.equals("amount")(payload.amount)(refund.amount);
    TestValidator.equals("currency")(payload.currency)(refund.currency);
    TestValidator.equals("status")(payload.status)(refund.status);
    TestValidator.equals("reason")(payload.reason)(refund.reason);
    TestValidator.equals("payment_id")(payload.payment_id)(refund.payment_id);
    TestValidator.equals("transaction_id")(payload.transaction_id)(refund.transaction_id);
    TestValidator.equals("order_id")(payload.order_id)(refund.order_id);
    TestValidator.predicate("requested_at exists")(() => typeof refund.requested_at === "string");
    TestValidator.equals("completed_at is null")(null)(refund.completed_at);
    // refund.id is a uuid string
    TestValidator.predicate("refund.id is uuid")(() => typeof refund.id === "string" && refund.id.length === 36);

    // Negative Case: Missing required fields
    await TestValidator.httpError("missing payment_id")(400)(() => 
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, payment_id: undefined as any })
    );
    await TestValidator.httpError("missing transaction_id")(400)(() => 
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, transaction_id: undefined as any })
    );
    await TestValidator.httpError("missing amount")(400)(() => 
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, amount: undefined as any })
    );
    await TestValidator.httpError("missing currency")(400)(() => 
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, currency: undefined as any })
    );
    await TestValidator.httpError("missing status")(400)(() => 
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, status: undefined as any })
    );

    // Negative Case: Negative amount
    await TestValidator.httpError("negative amount")(400)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, amount: -10 })
    );

    // Negative Case: Not a number for amount
    await TestValidator.httpError("non-number amount")(400)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, amount: "fake" as any })
    );

    // Negative Case: Empty/Invalid currency
    await TestValidator.httpError("empty currency")(400)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, currency: "" })
    );
    await TestValidator.httpError("null currency")(400)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, currency: null as any })
    );

    // Negative Case: Empty status
    await TestValidator.httpError("empty status")(400)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, status: "" })
    );

    // Negative Case: Blank payment_id & transaction_id
    await TestValidator.httpError("blank payment_id")(400)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, payment_id: "   " })
    );
    await TestValidator.httpError("blank transaction_id")(400)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, { ...payload, transaction_id: "" })
    );

    // Negative: refunding previously refunded payment (simulate by reusing id)
    await TestValidator.httpError("double refund not allowed")(400, 409)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, payload)
    );

    // Idempotency: attempt duplicate create (identical payload) and must get rejection or duplication error
    await TestValidator.httpError("exact duplicate create")(400, 409)(() =>
      api.functional.paymentsDiscounts.refunds.post(connection, payload)
    );
}

