  option_type: "size",
  value: "XL",
};
const updated = await api.functional.productCatalog.productOptions.put(connection, original.id, updateInput);
typia.assert<IProductOption>(updated);
TestValidator.equals("update attributes")(<IProductOption>{
  ...original,
  option_type: "size",
  value: "XL",
})(updated);

// 3. Update using non-existent/invalid ID (should 404)
