  const tagValues = [
    "Eco-Friendly",
    "2025 Summer",
    "Trending Now",
    "AI-generated: Popular",
    "1", // Non-English tag for coverage
  ];
  const createdTags: IProductTag[] = [];
  for (const value of tagValues) {
    const tag = await api.functional.productCatalog.productTags.post(connection, { value });
    typia.assert<IProductTag>(tag);
    createdTags.push(tag);
  }

  // 2. Retrieve all tags (no filter)
  const allTagsRes = await api.functional.productCatalog.productTags.patch(connection, {});
  typia.assert(allTagsRes);
  TestValidator.predicate("at least created tags appear")(() =>
    createdTags.every(ct => allTagsRes.data.some(rt => rt.id === ct.id && rt.value === ct.value))
  );
  TestValidator.predicate("pagination structure")(() =>
    typeof allTagsRes.pagination.current === "number" &&
    typeof allTagsRes.pagination.limit === "number" &&
    typeof allTagsRes.pagination.records === "number" &&
    typeof allTagsRes.pagination.pages === "number"
  );

  // 3. Fuzzy search by partial value
  const searchValue = "trend";
  const searchRes = await api.functional.productCatalog.productTags.patch(connection, { value: searchValue });
  typia.assert(searchRes);
  TestValidator.predicate("all returned tags contain search substring, case-insensitive")(() =>
    searchRes.data.every(tag => tag.value.toLowerCase().includes(searchValue))
  );

  // 4. Search for a value that doesn't exist
  const noResultRes = await api.functional.productCatalog.productTags.patch(connection, { value: "nonexistent-zzz" });
  typia.assert(noResultRes);
  TestValidator.equals("empty data on no matches")([] as IProductTag[])(noResultRes.data);

  // 5. Query first page with limit=1
  const pagedRes = await api.functional.productCatalog.productTags.patch(connection, { page: 1, limit: 1 });
  typia.assert(pagedRes);
  TestValidator.equals("one tag returned")([pagedRes.data[0]])(pagedRes.data);
  TestValidator.equals("limit is 1")(1)(pagedRes.pagination.limit);
  TestValidator.equals("current page is 1")(1)(pagedRes.pagination.current);

  // 6. Edge: Extremely long filter value, expect empty or no crash
  const longSearch = "x".repeat(300);
  const edgeRes = await api.functional.productCatalog.productTags.patch(connection, { value: longSearch });
  typia.assert(edgeRes);
  TestValidator.equals("extreme value yields empty")([] as IProductTag[])(edgeRes.data);

  // 7. Invalid params: negative page/limit
  await TestValidator.httpError("negative page", 400)(() => 
    api.functional.productCatalog.productTags.patch(connection, { page: -1 })
  );
  await TestValidator.httpError("zero/negative limit", 400)(() => 
    api.functional.productCatalog.productTags.patch(connection, { limit: 0 })
  );
  await TestValidator.httpError("negative limit", 400)(() => 
    api.functional.productCatalog.productTags.patch(connection, { limit: -5 })
  );
}

