export async function test_list_and_search_product_media_with_various_filters(connection: api.IConnection): Promise<void> {
    // 1. Create test records (with varied properties)
    const productId1 = "11111111-1111-1111-1111-111111111111";
    const productId2 = "22222222-2222-2222-2222-222222222222";
    const variantId1 = "33333333-3333-3333-3333-333333333333";
    const now = new Date();
    const isoNow = now.toISOString();
    const altA = "main thumbnail image (kr)";
    const altB = "secondary alt in en-us";
    // Entries: image/ko-KR, video/en-US, image/en-US
    const mediaA = await api.functional.productCatalog.productMedia.post(connection, {
        product_id: productId1,
        uri: "https://cdn.test.com/image-a.jpg",
        media_type: "image",
        locale: "ko-KR",
        alt_text: altA,
    });
    typia.assert<IProductMedia>(mediaA);
    const mediaB = await api.functional.productCatalog.productMedia.post(connection, {
        product_id: productId2,
        uri: "https://cdn.test.com/video-b.mp4",
        media_type: "video",
        locale: "en-US",
        alt_text: altB,
    });
    typia.assert<IProductMedia>(mediaB);
    const mediaC = await api.functional.productCatalog.productMedia.post(connection, {
        product_id: productId2,
        product_variant_id: variantId1,
        uri: "https://cdn.test.com/image-c.png",
        media_type: "image",
        locale: "en-US",
        alt_text: "gallery image third (en)",
    });
    typia.assert<IProductMedia>(mediaC);

    // 2. Basic listing (no filters)
    const listAll = await api.functional.productCatalog.productMedia.patch(connection, {});
    typia.assert<IPageIProductMedia>(listAll);
    TestValidator.predicate("listAll contains test entries")(() =>
        [mediaA.id, mediaB.id, mediaC.id].every(id => listAll.data.some(m => m.id === id))
    );
    // 3. Filter: media_type = 'image'
    const imgList = await api.functional.productCatalog.productMedia.patch(connection, { media_type: "image" });
    typia.assert<IPageIProductMedia>(imgList);
    TestValidator.predicate("media_type filter, only images")(() =>
        imgList.data.every(m => m.media_type === "image") && [mediaA.id, mediaC.id].every(id => imgList.data.some(m => m.id === id))
    );
    // 4. Filter: product_id
    const prod2List = await api.functional.productCatalog.productMedia.patch(connection, { product_id: productId2 });
    typia.assert<IPageIProductMedia>(prod2List);
    TestValidator.predicate("product_id filter")(() =>
        prod2List.data.every(m => m.product_id === productId2) && [mediaB.id, mediaC.id].every(id => prod2List.data.some(m => m.id === id))
    );
    // 5. Filter: locale
    const koList = await api.functional.productCatalog.productMedia.patch(connection, { locale: "ko-KR" });
    typia.assert<IPageIProductMedia>(koList);
    TestValidator.predicate("locale filter")(() =>
        koList.data.every(m => m.locale === "ko-KR") && koList.data.some(m => m.id === mediaA.id)
    );
    // 6. Filter: created_from (should return all, as 'now' is after creation)
    const past = new Date(now.getTime() - 1000 * 60 * 60 * 24).toISOString();
    const allFromPast = await api.functional.productCatalog.productMedia.patch(connection, { created_from: past });
    typia.assert<IPageIProductMedia>(allFromPast);
    TestValidator.predicate("created_from filter")(() =>
        [mediaA.id, mediaB.id, mediaC.id].every(id => allFromPast.data.some(m => m.id === id))
    );
    // 7. Filter: alt_text (partial match)
    const altList = await api.functional.productCatalog.productMedia.patch(connection, { alt_text: "main thum" });
    typia.assert<IPageIProductMedia>(altList);
    TestValidator.predicate("alt_text filter (partial)")(() =>
        altList.data.some(m => m.alt_text.includes("main thum")) && altList.data.some(m => m.id === mediaA.id)
    );
    // 8. Pagination: limit=2, offset=0
    const paged = await api.functional.productCatalog.productMedia.patch(connection, { limit: 2, offset: 0, sort: "created_at asc" });
    typia.assert<IPageIProductMedia>(paged);
    TestValidator.equals("pagination limit", undefined)(paged.data.length)(2);
    TestValidator.predicate("pagination metadata valid")(() =>
      typeof paged.pagination === "object" && paged.pagination.limit === 2
    );
    // 9. Edge: Empty result (nonexistent product_id)
    const noneList = await api.functional.productCatalog.productMedia.patch(connection, { product_id: "44444444-4444-4444-4444-444444444444" });
    typia.assert<IPageIProductMedia>(noneList);
    TestValidator.equals("empty data")<IProductMedia[]>([])(noneList.data);
    // 10. Edge: Invalid filter (negative offset)
    await TestValidator.httpError("invalid filter: negative offset")(400)(() =>
        api.functional.productCatalog.productMedia.patch(connection, { offset: -1 })
    );
}

