import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IInventory";
import { IPageIInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIInventory";
import typia from "typia";

export async function test_list_and_search_inventory_with_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // --- Preparation: create 2 inventory records with different variants ---
  const now = new Date();
  const variantA = "10000000-0000-0000-0000-0000000000a1";
  const variantB = "10000000-0000-0000-0000-0000000000b2";

  // First Inventory - Variant A
  const createA = await api.functional.productCatalog.inventory.post(connection, {
    product_variant_id: variantA,
    stock_count: 15,
    last_modified_at: now.toISOString(),
  });
  typia.assert<IInventory>(createA);
  // Second Inventory - Variant B
  const createB = await api.functional.productCatalog.inventory.post(connection, {
    product_variant_id: variantB,
    stock_count: 7,
    last_modified_at: now.toISOString(),
  });
  typia.assert<IInventory>(createB);

  // --- 1. List all inventories, no filters ---
  const resultAll = await api.functional.productCatalog.inventory.patch(connection, {});
  typia.assert<IPageIInventory>(resultAll);
  TestValidator.predicate("Should include the created inventories")(
    () => resultAll.data.some(x => x.id === createA.id && x.product_variant_id === variantA)
  );
  TestValidator.predicate("Should include the second created inventory")(
    () => resultAll.data.some(x => x.id === createB.id && x.product_variant_id === variantB)
  );

  // --- 2. Filter by product_variant_id (match one) ---
  const resultA = await api.functional.productCatalog.inventory.patch(connection, {
    product_variant_id: variantA,
  });
  typia.assert<IPageIInventory>(resultA);
  TestValidator.equals("Filter by product_variant_id should return only 1", undefined)(
    1
  )(resultA.data.length);
  TestValidator.equals("Return correct inventory", undefined)(
    createA.id
  )(resultA.data[0].id);

  // --- 3. Filter by stock range (stock_from, stock_to) ---
  const resultRange = await api.functional.productCatalog.inventory.patch(connection, {
    stock_from: 10,
    stock_to: 20,
  });
  typia.assert<IPageIInventory>(resultRange);
  TestValidator.predicate("Stock range filter returns createA only")(
    () => resultRange.data.some(x => x.product_variant_id === variantA && x.stock_count === 15)
  );
  TestValidator.predicate("Stock range should not return createB")(
    () => resultRange.data.every(x => x.product_variant_id !== variantB)
  );

  // --- 4. Apply impossible filter (nonexistent variant) ---
  const resultEmpty = await api.functional.productCatalog.inventory.patch(connection, {
    product_variant_id: "00000000-0000-0000-0000-000000999999",
  });
  typia.assert<IPageIInventory>(resultEmpty);
  TestValidator.equals("Empty result set for nonexistent variant", undefined)(
    0
  )(resultEmpty.data.length);

  // --- 5. Pagination: limit and offset ---
  const resultLimit = await api.functional.productCatalog.inventory.patch(connection, {
    limit: 1,
  });
  typia.assert<IPageIInventory>(resultLimit);
  TestValidator.equals("Pagination: limit", undefined)(
    1
  )(resultLimit.data.length);
  if (resultAll.data.length > 1) {
    const resultOffset = await api.functional.productCatalog.inventory.patch(connection, {
      offset: 1,
      limit: 1,
    });
    typia.assert<IPageIInventory>(resultOffset);
    TestValidator.equals("Pagination with offset", undefined)(
      1
    )(resultOffset.data.length);
    TestValidator.predicate("Offset does not return the previous record")(
      () => resultOffset.data[0].id !== resultAll.data[0].id
    );
  }
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IInventory";
import typia from "typia";

export async function test_get_inventory_by_id_with_valid_and_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // Presume we have a valid product_variant_id for association (in practice, this may require prior variant creation or retrieval)
  // For determinism, use a fixed-format UUID for test purposes
  const PRODUCT_VARIANT_ID = "55555555-5555-5555-5555-555555555555";
  const now = new Date().toISOString();
  const createInput: IInventory.ICreate = {
    product_variant_id: PRODUCT_VARIANT_ID,
    stock_count: 34,
    last_modified_at: now,
  };

  // Create an inventory record
  const created: IInventory = await api.functional.productCatalog.inventory.post(
    connection,
    { body: createInput },
  );
  typia.assert<IInventory>(created);
  TestValidator.equals("product_variant_id")(
    createInput.product_variant_id,
  )(created.product_variant_id);
  TestValidator.equals("stock_count")(
    createInput.stock_count,
  )(created.stock_count);
  TestValidator.equals("last_modified_at")(
    createInput.last_modified_at,
  )(created.last_modified_at);
  TestValidator.predicate("id is uuid")(() =>
    /^[\da-f]{8}-([\da-f]{4}-){3}[\da-f]{12}$/.test(created.id),
  );

  // GET by id should return same record
  const received: IInventory = await api.functional.productCatalog.inventory.get(
    connection,
    created.id,
  );
  typia.assert<IInventory>(received);
  TestValidator.equals("id")(
    created.id,
  )(received.id);
  TestValidator.equals("product_variant_id")(
    created.product_variant_id,
  )(received.product_variant_id);
  TestValidator.equals("stock_count")(
    created.stock_count,
  )(received.stock_count);
  TestValidator.equals("last_modified_at")(
    created.last_modified_at,
  )(received.last_modified_at);
  // Variant embedded, if returned
  if (received.variant) {
    TestValidator.equals("variant.id")(
      PRODUCT_VARIANT_ID,
    )(received.variant.id);
  }

  // Try non-existent id, expect 404
  const randomUUID = "00000000-0000-0000-0000-000000000404";
  await TestValidator.httpError("404 for non-existent ID")(404)(async () => {
    await api.functional.productCatalog.inventory.get(connection, randomUUID);
  });
}

