export async function test_update_inventory_by_id_with_various_conditions(connection: api.IConnection): Promise<void> {
  // 1. Create a valid inventory record
  const variantId = "11111111-1111-4111-8111-111111111111"; // Use a presumed existing variant UUID for test
  const createInput: IInventory.ICreate = {
    product_variant_id: variantId,
    stock_count: 100,
    last_modified_at: new Date().toISOString(),
  };
  const created = await api.functional.productCatalog.inventory.post(connection, createInput);
  typia.assert<IInventory>(created);
  TestValidator.equals("inventory.created.product_variant_id")(createInput.product_variant_id)(created.product_variant_id);
  TestValidator.equals("inventory.created.stock_count")(createInput.stock_count)(created.stock_count);

  // 2. Update with valid new stock_count and changed timestamp
  const newStock = 60;
  const updateInput: IInventory.IUpdate = {
    stock_count: newStock,
    last_modified_at: new Date(Date.now() + 1000).toISOString(),
  };
  const updated = await api.functional.productCatalog.inventory.put(connection, created.id, updateInput);
  typia.assert<IInventory>(updated);
  TestValidator.equals("inventory.updated.id")(created.id)(updated.id);
  TestValidator.equals("inventory.updated.product_variant_id")(created.product_variant_id)(updated.product_variant_id);
  TestValidator.equals("inventory.updated.stock_count")(updateInput.stock_count!)(updated.stock_count);
  if (updateInput.last_modified_at)
    TestValidator.equals("updated.last_modified_at")(updateInput.last_modified_at)(updated.last_modified_at);

  // 3. Attempt update with missing attributes (empty input object)
  await TestValidator.httpError("put with missing attributes")(400)(
    () => api.functional.productCatalog.inventory.put(connection, created.id, {})
  );

  // 4. Attempt update with invalid negative stock_count
