    () => api.functional.productCatalog.inventory.put(connection, created.id, {
      stock_count: -5,
      last_modified_at: new Date().toISOString(),
    })
  );

  // 5. Attempt update on non-existent inventory id
  await TestValidator.httpError("put to non-existent id")(404)(
    () => api.functional.productCatalog.inventory.put(connection, "99999999-9999-4999-8999-999999999999", updateInput)
  );
}

