import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IImportFeed";
import { IPageIImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIImportFeed";
typia from "typia";

export async function test_list_and_search_import_feeds_with_various_conditions(connection: api.IConnection): Promise<void> {
    // Create several import feed records
    const feeds: IImportFeed[] = [];
    const now = new Date();
    const baseDateStr = now.toISOString();
    const feedInputs = [
        {
            source_name: "Google Shopping",
            source_uri: "https://feeds.example.com/google",
            imported_at: baseDateStr
        },
        {
            source_name: "Partner API",
            source_uri: "https://feeds.partner.com/import1",
            imported_at: new Date(now.getTime() - 1000 * 60 * 60 * 24).toISOString() // 1 day ago
        },
        {
            source_name: "Legacy Upload",
            source_uri: "file:///tmp/import-legacy.csv",
            imported_at: new Date(now.getTime() - 1000 * 60 * 60 * 48).toISOString() // 2 days ago
        }
    ];
    for (const input of feedInputs) {
        const created = await api.functional.productCatalog.importFeeds.post(connection, input);
        typia.assert<IImportFeed>(created);
        feeds.push(created);
    }

    // 1. Unfiltered: should return all created feeds (paginated)
    const allResult = await api.functional.productCatalog.importFeeds.patch(connection, { page: 1, limit: 10 });
    typia.assert<IPageIImportFeed>(allResult);
    TestValidator.predicate("returns all feeds")(() => Array.isArray(allResult.data) && feeds.every(f => allResult.data.some(r => r.id === f.id)));

    // 2. Filter by exact source_name
    const googleRes = await api.functional.productCatalog.importFeeds.patch(connection, { source_name: "Google Shopping" });
    typia.assert<IPageIImportFeed>(googleRes);
    TestValidator.predicate("filter by source_name")(() => googleRes.data.length === 1 && googleRes.data[0].source_name === "Google Shopping");
    TestValidator.equals("correct feed by name")<IImportFeed>(feeds[0])(googleRes.data[0]);

    // 3. Date range filter (imported_from and imported_to)
    // Only "Partner API" should match this range
    const dayAgo = new Date(now.getTime() - 1000 * 60 * 60 * 24 - 100).toISOString();
    const nowPlus = new Date(now.getTime() + 1000).toISOString();
    const dateRangeRes = await api.functional.productCatalog.importFeeds.patch(connection, { imported_from: dayAgo, imported_to: nowPlus });
    typia.assert<IPageIImportFeed>(dateRangeRes);
    TestValidator.predicate("date range filter includes feeds 0 and 1")(
        () => dateRangeRes.data.some(r => r.id === feeds[0].id) && dateRangeRes.data.some(r => r.id === feeds[1].id)
    );

    // 4. Search yielding no records
    const emptyRes = await api.functional.productCatalog.importFeeds.patch(connection, { source_name: "NonExistentFeed" });
    typia.assert<IPageIImportFeed>(emptyRes);
    TestValidator.equals("empty array for empty search")<IImportFeed[]>([])(emptyRes.data);

    // 5. Pagination test: limit=1, check pagination metadata and sequential calls
    const pageOne = await api.functional.productCatalog.importFeeds.patch(connection, { page: 1, limit: 1 });
    typia.assert<IPageIImportFeed>(pageOne);
    TestValidator.equals("first page feed count")<number>(1)(pageOne.data.length);
    TestValidator.equals("pagination page 1")<number>(1)(pageOne.pagination.current);
    if (pageOne.pagination.pages > 1) {
        const pageTwo = await api.functional.productCatalog.importFeeds.patch(connection, { page: 2, limit: 1 });
        typia.assert<IPageIImportFeed>(pageTwo);
        TestValidator.equals("page two feed count")<number>(1)(pageTwo.data.length);
        TestValidator.equals("pagination page 2")<number>(2)(pageTwo.pagination.current);
    }
}

