import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IImportFeed";
import typia from "typia";

export async function test_update_import_feed_by_id_with_various_conditions(connection: api.IConnection): Promise<void> {
    // 1. Prerequisite: Create a valid import feed to update
    const createInput: IImportFeed.ICreate = {
        source_name: "Test Feed",
        source_uri: "https://test.example.com/feed1.xml",
        imported_at: new Date().toISOString()
    };
    const original = await api.functional.productCatalog.importFeeds.post(connection, createInput);
    typia.assert<IImportFeed>(original);

    // 2. Positive Update: update source_name and imported_at
    const updateInput: IImportFeed.IUpdate = {
        source_name: "Updated Feed Name",
        imported_at: new Date(Date.now() - 3600000).toISOString(), // 1 hour earlier
    };
    const updated = await api.functional.productCatalog.importFeeds.put(connection, original.id, updateInput);
    typia.assert<IImportFeed>(updated);
    TestValidator.equals("should update source_name")<string>(updateInput.source_name!)(updated.source_name);
    TestValidator.equals("should update imported_at")<string>(updateInput.imported_at!)(updated.imported_at);
    TestValidator.equals("id must not change")<string>(original.id)(updated.id);
    TestValidator.equals("source_uri must not change")<string>(original.source_uri)(updated.source_uri);

    // 3.a Invalid Update: empty body (should do nothing or respond with error)
    await TestValidator.httpError("Expect validation error on empty body")(400,422)(async () => {
        await api.functional.productCatalog.importFeeds.put(connection, original.id, {} as IImportFeed.IUpdate);
    });

    // 3.b Invalid Update: malformed 'imported_at'
    const invalidUpdate: IImportFeed.IUpdate = {
