import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICatalogEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvent";
import { IPageICatalogEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICatalogEvent";
import typia from "typia";

function randomUuid() {
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, function (c) {
    var r = (Math.random() * 16) | 0,
      v = c === "x" ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

function isoNow(offsetMinutes = 0) {
  return new Date(Date.now() + offsetMinutes * 60000).toISOString();
}

export async function test_api_list_and_search_catalog_events_with_various_conditions(connection: api.IConnection): Promise<void> {
  // 1. Create catalog event records
  const userId1 = randomUuid();
  const userId2 = randomUuid();
  const productIdA = randomUuid();
  const variantIdA = randomUuid();
  const now = isoNow();
  const earlier = isoNow(-1000);

  const catalogEvents: ICatalogEvent[] = [];

  const eventInputs = [
    {
      user_id: userId1,
      event_type: "create",
      description: "Product created",
      product_id: productIdA,
      product_variant_id: null,
      occurred_at: earlier,
    },
    {
      user_id: userId1,
      event_type: "update",
      description: "Product updated",
      product_id: productIdA,
      product_variant_id: variantIdA,
      occurred_at: now,
    },
    {
      user_id: userId2,
      event_type: "import",
      description: "Product imported",
      product_id: null,
      product_variant_id: null,
      occurred_at: now,
    },
  ];

  for (const input of eventInputs) {
    const created = await api.functional.productCatalog.catalogEvents.post(connection, input);
    typia.assert<ICatalogEvent>(created);
    catalogEvents.push(created);
  }

  // 2a. List all events (no filters)
  {
    const req = {};
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("List all - count matches")(res.data.length >= catalogEvents.length);
  }

  // 2b. Filter by event_type ("create")
  {
    const req = { event_type: "create" };
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("event_type filter")(res.data.every(ev => ev.event_type === "create"));
  }

  // 2c. Filter by user_id (userId1)
  {
    const req = { user_id: userId1 };
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("user_id filter")(res.data.every(ev => ev.user_id === userId1));
  }

  // 2d. Pagination - limit=1
  {
    const req = { limit: 1, page: 1 };
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.equals("pagination limit 1")(1)(res.data.length);
    TestValidator.equals("pagination page 1")(1)(res.pagination.current);
  }

  // 2e. Combined filters (event_type="update", user_id=userId1)
  {
    const req = { event_type: "update", user_id: userId1 };
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("combined filter")(res.data.every(ev => ev.event_type === "update" && ev.user_id === userId1));
  }

  // 2f. Edge: event_type that doesn't exist
  {
    const req = { event_type: "nonexistent-type" };
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.equals("empty data for non-existent event_type")(0)(res.data.length);
  }

  // 2g. Edge: impossible time range (from > to)
  {
    const req = { from: now, to: earlier };
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.equals("empty data for impossible range")(0)(res.data.length);
  }

  // 2h. Edge: invalid page/limit (negative)
  {
    const req = { limit: -5, page: -2 };
    await TestValidator.httpError("invalid page/limit") (400) (() => api.functional.productCatalog.catalogEvents.patch(connection, req));
  }

  // 2h (cont). Edge: huge page number
  {
    const req = { limit: 2, page: 999 };
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, req);
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.equals("empty data for excessive page")(0)(res.data.length);
  }
}

