  // 1. Prepare valid event data
  const now = new Date().toISOString();
  const validInput: ICatalogEvent.ICreate = {
    product_id: "a93f376e-8531-4bf2-82d8-1ad1e3b7b101",
    product_variant_id: null,
    user_id: "73f8ba97-807e-491f-bdb2-c5eb1ce54203",
    event_type: "creation",
    description: "Initial product creation for audit",
    occurred_at: now,
  };

  // 2. Successful creation
  const created = await api.functional.productCatalog.catalogEvents.post(connection, validInput);
  typia.assert<ICatalogEvent>(created);
  TestValidator.predicate("Catalog event id generated")(() => typeof created.id === "string" && created.id.length > 0);
  TestValidator.equals("Catalog event user_id")(validInput.user_id)(created.user_id);
  TestValidator.equals("Catalog event event_type")(validInput.event_type)(created.event_type);
  TestValidator.equals("Catalog event description")(validInput.description)(created.description);
  TestValidator.equals("Catalog event occurred_at")(validInput.occurred_at)(created.occurred_at);
  // Optional fields
  TestValidator.equals("Catalog event product_id")(validInput.product_id)(created.product_id);
  TestValidator.equals("Catalog event product_variant_id")(validInput.product_variant_id)(created.product_variant_id);

  // 3. Missing required field: user_id omitted
  const invalidMissingUser: ICatalogEvent.ICreate = {
    ...validInput,
  };
  // @ts-expect-error: Test validation, user_id is omitted
  delete (invalidMissingUser as any).user_id;
  await TestValidator.httpError("Missing required user_id field")(400)(async () => {
    await api.functional.productCatalog.catalogEvents.post(connection, invalidMissingUser as any);
  });

