  const invalidUUID: ICatalogEvent.ICreate = {
    ...validInput,
    user_id: "not-a-uuid",
  };
  await TestValidator.httpError("Invalid UUID for user_id")(400, 422)(async () => {
    await api.functional.productCatalog.catalogEvents.post(connection, invalidUUID);
  });

  // 5. Empty description
  const invalidDescription: ICatalogEvent.ICreate = {
    ...validInput,
    description: "",
  };
  await TestValidator.httpError("Empty description error")(400, 422)(async () => {
    await api.functional.productCatalog.catalogEvents.post(connection, invalidDescription);
  });

  // 6. Invalid event_type (not in allowed enum)
  const invalidEventType: ICatalogEvent.ICreate = {
    ...validInput,
    event_type: "not_a_real_type",
  };
  await TestValidator.httpError("Invalid event_type")(400, 422)(async () => {
    await api.functional.productCatalog.catalogEvents.post(connection, invalidEventType);
  });

