      const duplicate = await api.functional.productCatalog.catalogEvents.post(connection, validInput);
      typia.assert<ICatalogEvent>(duplicate);
      // If duplicate is permitted but new id must be different
      return duplicate.id !== created.id; // Should not allow duplicate ID
    } catch (err) {
      // If API returns error, consider this correct (uniqueness enforced)
      return true;
    }
  });
}

