import { IOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderLog";
import { IPageIOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrderLog";

export async function test_list_and_filter_order_logs_with_pagination_and_permissions(connection: api.IConnection): Promise<void> {
    // 1. Create test order_log records with varied filtering fields
    const now = new Date();
    const orderIds = [
        "00000000-1111-2222-3333-444444444444",
        "10000000-1111-2222-3333-444444444444",
    ];
    const actorIds = [
        "a0000000-1111-2222-3333-444444444444",
        "b0000000-1111-2222-3333-444444444444",
    ];
    const eventTypes = ["create", "update", "status_change"];
    const logs: IOrderLog[] = [];
    for (let i = 0; i < 8; ++i) {
        const input: IOrderLog.ICreate = {
            order_id: orderIds[i % orderIds.length],
            evidence_id: null,
            event_type: eventTypes[i % eventTypes.length],
            event_description: `event ${i}`,
            event_time: new Date(now.getTime() - i * 60000).toISOString(), // Each log spaced by 1 min
            actor_id: actorIds[i % actorIds.length],
        };
        const created = await api.functional.cartOrder.orderLog.post(connection, input);
        typia.assert<IOrderLog>(created);
        logs.push(created);
    }

    // 2. General search (all logs; paginate - limit 3)
    let res = await api.functional.cartOrder.orderLog.patch(connection, { limit: 3, page: 1 });
    typia.assert<IPageIOrderLog>(res);
    TestValidator.predicate("General search returns some logs")(() => res.data.length > 0);
    TestValidator.equals("Correct page 1 size")(3)(res.data.length);
    TestValidator.equals("pagination reflects first page")(1)(res.pagination.current);
    TestValidator.equals("pagination total records")(logs.length)(res.pagination.records);
    for (const log of res.data) typia.assert<IOrderLog>(log);

    // 3. Next page (page 2)
    const page2 = await api.functional.cartOrder.orderLog.patch(connection, { limit: 3, page: 2 });
    typia.assert<IPageIOrderLog>(page2);
    TestValidator.equals("Correct page 2 size")(3)(page2.data.length);
    TestValidator.equals("pagination second page")(2)(page2.pagination.current);
    for (const log of page2.data) typia.assert<IOrderLog>(log);

    // 4. Out-of-range page
    const outPage = await api.functional.cartOrder.orderLog.patch(connection, { limit: 3, page: 10 });
    typia.assert<IPageIOrderLog>(outPage);
    TestValidator.equals("Empty result on OOR page")([] as IOrderLog[])(outPage.data);
    TestValidator.equals("pagination at OOR page")(10)(outPage.pagination.current);

    // 5. Filter by order_id (should match half of the logs)
    const orderIdFilter = await api.functional.cartOrder.orderLog.patch(connection, { order_id: orderIds[0] });
    typia.assert<IPageIOrderLog>(orderIdFilter);
    for (const log of orderIdFilter.data) {
        typia.assert<IOrderLog>(log);
        TestValidator.equals("order_id filter matches")(orderIds[0])(log.order_id);
    }

    // 6. Filter by event_type
    const eventTypeFilter = await api.functional.cartOrder.orderLog.patch(connection, { event_type: "status_change" });
    typia.assert<IPageIOrderLog>(eventTypeFilter);
