import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderLog";
import typia from "typia";

export async function test_update_order_log_event_and_validate_allowed_fields(connection: api.IConnection): Promise<void> {
    // Step 1: Create a valid order_log entry first
    const order_id = "c1111111-1111-4111-b111-111111111111";
    const actor_id = "d2222222-2222-4222-b222-222222222222";
    const evidence_id = "e3333333-3333-4333-b333-333333333333";
    const event_type = "create";
    const event_description = "Initial event log for creation.";
    const event_time = new Date().toISOString();

    const createInput: IOrderLog.ICreate = {
        order_id,
        actor_id,
        evidence_id,
        event_type,
        event_description,
        event_time
    };
    const created: IOrderLog = await api.functional.orderLog.post(connection, createInput);
    typia.assert<IOrderLog>(created);

    // Step 2: Update allowed fields
    const updateInput: IOrderLog.IUpdate = {
        event_type: "update",
        event_description: "This log was updated for test.",
        event_time: new Date(Date.now() + 10000).toISOString(), // advance time by 10s
        actor_id: actor_id,
        evidence_id: evidence_id,
    };
    const updated = await api.functional.orderLog.put(connection, created.id, updateInput);
    typia.assert<IOrderLog>(updated);
    TestValidator.equals("id unchanged")(created.id)(updated.id);
    TestValidator.equals("order_id unchanged")(created.order_id)(updated.order_id);
    TestValidator.equals("event_type updated")(updateInput.event_type)(updated.event_type);
    TestValidator.equals("event_description updated")((updateInput.event_description || ""))(updated.event_description);
    TestValidator.equals("event_time updated")(updateInput.event_time)(updated.event_time);
    TestValidator.equals("actor_id updated")(updateInput.actor_id)(updated.actor_id);
    TestValidator.equals("evidence_id updated")(updateInput.evidence_id)(updated.evidence_id);

    // Step 3: Partial update: only event_description
    const partialUpdate: IOrderLog.IUpdate = {
        event_description: "Partial update only description."
    };
    const partiallyUpdated = await api.functional.orderLog.put(connection, created.id, partialUpdate);
    typia.assert<IOrderLog>(partiallyUpdated);
    TestValidator.equals("id unchanged")(created.id)(partiallyUpdated.id);
    TestValidator.equals("order_id unchanged")(created.order_id)(partiallyUpdated.order_id);
    TestValidator.equals("partial event_description update")(partialUpdate.event_description)(partiallyUpdated.event_description);

    // Step 4: Attempt to update immutable fields (should fail)
    await TestValidator.httpError("cannot update id or order_id")(400)(async () => {
        // @ts-expect-error - id/order_id are not fields in IOrderLog.IUpdate, but simulate overposting
        await api.functional.orderLog.put(connection, created.id, { id: "malicious", order_id: "malicious" });
    });

    // Step 5: Attempt to update non-existent order_log
    await TestValidator.httpError("update non-existent order_log")(404)(async () => {
        await api.functional.orderLog.put(connection, "00000000-0000-4000-8000-000000000000", updateInput);
    });
}

