import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IOrderEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderEvidence";
import { IPageIOrderEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrderEvidence";

export async function test_search_order_evidence_with_pagination_and_authorization(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Seed data
  const now = new Date();
  const orderId1 = "b1d7d6ed-0000-4a3a-b5b0-6a10ea892001";
  const orderId2 = "7633fa7f-0000-43ae-82ed-44ca27ac8002";
  const provider1 = "0d5b684e-0000-40cd-84e4-bcb9d2ac0001";
  const provider2 = "6f944d38-0000-49e2-bad7-117a241e0002";
  const timestamp1 = new Date(now.getTime() - 1000 * 60 * 60 * 24 * 2).toISOString();
  const timestamp2 = new Date(now.getTime() - 1000 * 60 * 60 * 24 * 1).toISOString();
  const timestamp3 = now.toISOString();

  const records: IOrderEvidence[] = [];
  // Create 4 order evidence records
  records.push(
    await api.functional.cartOrder.orderEvidence.post(connection, {
      order_id: orderId1,
      evidence_type: "invoice",
      evidence_uri: "s3://evidence/invoice1.pdf",
      provided_at: timestamp1,
      provided_by: provider1,
    })
  );
  records.push(
    await api.functional.cartOrder.orderEvidence.post(connection, {
      order_id: orderId1,
      evidence_type: "photo",
      evidence_uri: "s3://evidence/photo1.jpeg",
      provided_at: timestamp2,
      provided_by: provider2,
    })
  );
  records.push(
    await api.functional.cartOrder.orderEvidence.post(connection, {
      order_id: orderId2,
      evidence_type: "receipt",
      evidence_uri: "s3://evidence/receipt1.pdf",
      provided_at: timestamp3,
      provided_by: provider1,
    })
  );
  records.push(
    await api.functional.cartOrder.orderEvidence.post(connection, {
      order_id: orderId2,
      evidence_type: "signature",
      evidence_uri: "s3://evidence/sig1.png",
      provided_at: timestamp3,
      provided_by: null,
    })
  );

  // Step 2: Basic search (no filters)
  const basicSearch = await api.functional.cartOrder.orderEvidence.patch(connection, {});
  typia.assert<IPageIOrderEvidence>(basicSearch);
  TestValidator.predicate("Should return seeded records")(() =>
    basicSearch.data.some((e) => records.map((r) => r.id).includes(e.id))
  );

  // Step 3: Filter by order_id
  const orderIdSearch = await api.functional.cartOrder.orderEvidence.patch(connection, { order_id: orderId1 });
  typia.assert<IPageIOrderEvidence>(orderIdSearch);
  TestValidator.predicate("Should only return records for orderId1")(() =>
    orderIdSearch.data.every((e) => e.order_id === orderId1)
  );

  // Step 4: Filter by evidence_type
  const typeSearch = await api.functional.cartOrder.orderEvidence.patch(connection, { evidence_type: "invoice" });
  typia.assert<IPageIOrderEvidence>(typeSearch);
  TestValidator.predicate("Should only return invoice evidence")(() =>
    typeSearch.data.every((e) => e.evidence_type === "invoice")
  );

  // Step 5: Date range filter (from_time, to_time)
  const rangeSearch = await api.functional.cartOrder.orderEvidence.patch(connection, { from_time: timestamp2, to_time: timestamp3 });
  typia.assert<IPageIOrderEvidence>(rangeSearch);
  TestValidator.predicate("Should only include records in range")(() =>
    rangeSearch.data.every((e) => e.provided_at >= timestamp2 && e.provided_at < timestamp3)
  );

  // Step 6: Filter by provided_by
  const providerSearch = await api.functional.cartOrder.orderEvidence.patch(connection, { provided_by: provider1 });
  typia.assert<IPageIOrderEvidence>(providerSearch);
  TestValidator.predicate("Should only include records by provider1")(() =>
    providerSearch.data.every((e) => e.provided_by === provider1)
  );

  // Step 7: Combined filters
  const combinedSearch = await api.functional.cartOrder.orderEvidence.patch(connection, { order_id: orderId1, evidence_type: "photo" });
  typia.assert<IPageIOrderEvidence>(combinedSearch);
  TestValidator.predicate("Should only return matching combined records")(() =>
    combinedSearch.data.every((e) => e.order_id === orderId1 && e.evidence_type === "photo")
  );

  // Step 8: Pagination
  const paged = await api.functional.cartOrder.orderEvidence.patch(connection, { page: 1, limit: 2 });
  typia.assert<IPageIOrderEvidence>(paged);
  TestValidator.equals("Should return 2 records or fewer")(
    true
  )(paged.data.length <= 2);
  if (paged.pagination.pages > 1) {
    const paged2 = await api.functional.cartOrder.orderEvidence.patch(connection, { page: 2, limit: 2 });
    typia.assert<IPageIOrderEvidence>(paged2);
    TestValidator.equals("Should return paged results on page 2")(
      true
    )(paged2.data.length > 0);
  }

  // Step 9: Empty results
  const emptySearch = await api.functional.cartOrder.orderEvidence.patch(connection, { evidence_type: "NO_SUCH_TYPE" });
  typia.assert<IPageIOrderEvidence>(emptySearch);
  TestValidator.equals("Should return empty for nonexistent type")(
    [] as IOrderEvidence[]
  )(emptySearch.data);

  // Step 10: Invalid filter (bad UUID)
  await TestValidator.httpError("Should error on invalid UUID")(400)(async () => {
    return api.functional.cartOrder.orderEvidence.patch(connection, { order_id: "not-a-uuid" });
  });

  // Step 11: Authorization checks (simulate by using an unauthenticated connection if possible)
  // This is pseudo-code since unauthenticated connection simulation depends on test infra
  if (typeof (connection as any).unauthorized === "function") {
    await TestValidator.httpError("Should reject unauthorized users")(401, 403)(async () => {
      return api.functional.cartOrder.orderEvidence.patch((connection as any).unauthorized(), {});
    });
  }
}

