import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPayment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPayment";
import typia from "typia";

export async function test_api_search_and_paginate_payments_with_various_filters(connection: api.IConnection): Promise<void> {
  // 1. Seed data with several payments
  const seedPayments: IPayment[] = [];
  const now = new Date();
  for (let i = 0; i < 4; i++) {
    const baseDate = new Date(now.getTime() - i * 86400000); // Each day prior
    const paymentInput = {
      transaction_id: `${i + 1}`.repeat(8).padEnd(36, '0'), // fake uuid
      payment_method_id: `${i + 2}`.repeat(8).padEnd(36, '1'),
      amount: 100 * (i + 1),
      currency: i % 2 === 0 ? "USD" : "KRW",
      description: `Seed payment #${i + 1}`,
    };
    const created: IPayment = await api.functional.paymentsDiscounts.payments.post(connection, paymentInput);
    seedPayments.push(created);
  }

  // 2. Retrieve all payments (no filter) and assert all seeded payments are present
  const resAll = await api.functional.paymentsDiscounts.payments.patch(connection, {});
  typia.assert(resAll);
  TestValidator.predicate("All seeded payments returned")(() => {
    return seedPayments.every(seed => resAll.data.some(r => r.id === seed.id));
  });

  // 3. Search by user_id (simulate using payment_method_id, as user_id is not settable here)
  const paymentMethodId = seedPayments[1].payment_method_id;
  const resByMethod = await api.functional.paymentsDiscounts.payments.patch(connection, { payment_method_id: paymentMethodId });
  typia.assert(resByMethod);
  TestValidator.predicate("Filtered by payment_method_id only correct records returned")(() =>
    resByMethod.data.every(r => r.payment_method_id === paymentMethodId)
  );

  // 4. Search by status (simulate using status from existing record)
  const status = seedPayments[0].status;
  const resByStatus = await api.functional.paymentsDiscounts.payments.patch(connection, { status });
  typia.assert(resByStatus);
  TestValidator.predicate("Filtered by status only correct records returned")(() =>
    resByStatus.data.every(r => r.status === status)
  );

  // 5. Search by date range
  const dateFrom = seedPayments[2].initiated_at;
  const dateTo = seedPayments[0].initiated_at;
  const resDate = await api.functional.paymentsDiscounts.payments.patch(connection, {
    initiated_from: dateFrom,
    initiated_to: dateTo,
  });
  typia.assert(resDate);
  TestValidator.predicate("Date range filter correct subset")(() =>
    resDate.data.every(r => r.initiated_at >= dateFrom && r.initiated_at < dateTo)
  );

  // 6. Paginate with page/limit
  const resPaginated = await api.functional.paymentsDiscounts.payments.patch(connection, { page: 1, limit: 2 });
  typia.assert(resPaginated);
  TestValidator.equals("Pagination: correct count")(2)(resPaginated.data.length);

  // 7. Improper input: invalid user_id (simulate with bad uuid)
  await TestValidator.error("Invalid user_id filter")(() =>
    api.functional.paymentsDiscounts.payments.patch(connection, { user_id: "not-a-uuid" })
  );

  // 8. Improper input: nonsensical date range (from after to)
  await TestValidator.error("Nonsensical date range")(() =>
    api.functional.paymentsDiscounts.payments.patch(connection, {
      initiated_from: "2099-01-01T00:00:00Z",
      initiated_to: "1999-01-01T00:00:00Z",
    })
  );
}

