import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrder";
import { IPayment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPayment";
import typia from "typia";

export async function test_create_payment_with_all_permutations_of_valid_and_invalid_input(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a valid order to obtain a valid transaction_id
  const orderInput: ICartOrderOrder.ICreate = {
    user_id: "00000000-0000-4000-8000-000000000001",
    cart_id: "00000000-0000-4000-8000-000000000002"
  };
  const order = await api.functional.cartOrder.order.post(
    connection,
    orderInput
  );
  typia.assert<ICartOrderOrder>(order);

  // 2. Prepare valid payment input
  const validPaymentInput: IPayment.ICreate = {
    transaction_id: order.id,
    payment_method_id: "00000000-0000-4000-8000-000000000003",
    amount: 100.0,
    currency: "USD",
    description: "Test payment"
  };

  // 3. Positive: create payment successfully
  const payment = await api.functional.paymentsDiscounts.payments.post(
    connection,
    validPaymentInput
  );
  typia.assert<IPayment>(payment);
  TestValidator.equals("transaction_id should match order")(payment.transaction_id)(order.id);
  TestValidator.equals("amount should match")(payment.amount)(validPaymentInput.amount);
  TestValidator.equals("currency should match")(payment.currency)(validPaymentInput.currency);

  // 4. Negative: missing required field (amount)
  await TestValidator.httpError("missing amount should fail")(400)(async () => {
    const { amount, ...noAmountPayload } = validPaymentInput;
    // @ts-expect-error: deliberately missing amount
    await api.functional.paymentsDiscounts.payments.post(connection, noAmountPayload);
  });

  // 5. Negative: invalid transaction_id (simulate non-existent UUID)
  await TestValidator.httpError("invalid transaction_id should fail")(400,404)(async () => {
    const invalidTxPayload = { ...validPaymentInput, transaction_id: "00000000-0000-4000-8000-999999999999" };
    await api.functional.paymentsDiscounts.payments.post(connection, invalidTxPayload);
  });

  // 6. Negative: invalid payment_method_id (simulate non-existent UUID)
  await TestValidator.httpError("invalid payment_method_id should fail")(400,404)(async () => {
    const invalidPmPayload = { ...validPaymentInput, payment_method_id: "00000000-0000-4000-8000-888888888888" };
    await api.functional.paymentsDiscounts.payments.post(connection, invalidPmPayload);
  });

  // 7. Edge: duplicate payment (same transaction_id/payment_method_id)
  await TestValidator.httpError("duplicate payment should fail")(400,409)(async () => {
    await api.functional.paymentsDiscounts.payments.post(connection, validPaymentInput);
  });

  // 8. Edge: negative amount
  await TestValidator.httpError("negative amount should fail")(400)(async () => {
    const negativeAmountPayload = { ...validPaymentInput, amount: -50.0 };
    await api.functional.paymentsDiscounts.payments.post(connection, negativeAmountPayload);
  });

  // 9. Edge: invalid currency code
  await TestValidator.httpError("invalid currency should fail")(400)(async () => {
    const invalidCurrencyPayload = { ...validPaymentInput, currency: "INVALID" };
    await api.functional.paymentsDiscounts.payments.post(connection, invalidCurrencyPayload);
  });

  // 10. Edge: null in required fields
  await TestValidator.httpError("null transaction_id should fail")(400)(async () => {
    const nullTxPayload = { ...validPaymentInput, transaction_id: null as any };
    await api.functional.paymentsDiscounts.payments.post(connection, nullTxPayload);
  });
  await TestValidator.httpError("null payment_method_id should fail")(400)(async () => {
    const nullPmPayload = { ...validPaymentInput, payment_method_id: null as any };
    await api.functional.paymentsDiscounts.payments.post(connection, nullPmPayload);
  });
  await TestValidator.httpError("null amount should fail")(400)(async () => {
    const nullAmountPayload = { ...validPaymentInput, amount: null as any };
    await api.functional.paymentsDiscounts.payments.post(connection, nullAmountPayload);
  });
  await TestValidator.httpError("null currency should fail")(400)(async () => {
    const nullCurrencyPayload = { ...validPaymentInput, currency: null as any };
    await api.functional.paymentsDiscounts.payments.post(connection, nullCurrencyPayload);
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPayment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPayment";
import typia from "typia";

export async function test_update_payment_metadata_and_enforce_integrity(connection: api.IConnection): Promise<void> {
    // 1. Seed payment record
    const createInput: IPayment.ICreate = {
        transaction_id: "test-txn-1000-uuid",
        payment_method_id: "test-pay-method-uuid-9991",
        amount: 100,
        currency: "USD",
        description: "Seed payment for update test"
    };

    const created = await api.functional.paymentsDiscounts.payments.post(connection, createInput);
    typia.assert<IPayment>(created);

    // 2. Update allowed fields successfully
    const updateInput: IPayment.IUpdate = {
        payment_method_id: "test-pay-method-uuid-9992",
        completed_at: new Date().toISOString(),
        description: "Admin completion of payment metadata"
    };
    const paymentId: string = created.id;

    const updated = await api.functional.paymentsDiscounts.payments.put(connection, paymentId, updateInput);
    typia.assert<IPayment>(updated);
    // Validate returned payment matches updates on allowed fields
    TestValidator.equals("payment_method_id")<string>(updateInput.payment_method_id!)(updated.payment_method_id);
    TestValidator.equals("completed_at")<string | null>(updateInput.completed_at!)(updated.completed_at);
    TestValidator.equals("description")<string | null>(updateInput.description!)(updated.description);
    // Validate immutable fields are unchanged
    TestValidator.equals("amount")<number>(created.amount)(updated.amount);
    TestValidator.equals("transaction_id")<string>(created.transaction_id)(updated.transaction_id);
    TestValidator.equals("currency")<string>(created.currency)(updated.currency);
    TestValidator.equals("status")<string>(created.status)(updated.status);
    TestValidator.equals("initiated_at")<string>(created.initiated_at)(updated.initiated_at);
    // 3. Attempt to update immutable fields (should be rejected)
    const illegalUpdate: any = {
        amount: 99999,
        transaction_id: "malicious-txn-id",
        status: "refunded"
    };
    await TestValidator.error("immutable field update rejected")(() =>
        api.functional.paymentsDiscounts.payments.put(connection, paymentId, illegalUpdate)
    );
    // 4. Attempt to update non-existent payment id (should throw 404 or equivalent)
    await TestValidator.httpError("non-existent payment update") (404)(() =>
        api.functional.paymentsDiscounts.payments.put(
            connection,
            "00000000-0000-4000-8000-000000000404",
            updateInput
        )
    );
}

