import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPaymentMethod } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentMethod";
import { IPageIPaymentMethod } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPaymentMethod";
import typia from "typia";

export async function test_search_and_list_payment_methods_by_ownership(connection: api.IConnection): Promise<void> {
    // 1. Create users (simulate with uuids)
    const userA = "a7636be2-9ac8-4f5b-bc12-1d4be1f7e321";
    const userB = "e72f2cdd-7933-4ddc-8352-cb2d5b258965";
    // 2. Create payment methods for each user
    const pmA_active = await api.functional.paymentsDiscounts.paymentMethods.post(connection, {
        user_id: userA,
        type: "VISA",
        masked_detail: "**** **** **** 1234",
        provider: "Stripe",
        active: true,
    });
    typia.assert<IPaymentMethod>(pmA_active);
    const pmA_inactive = await api.functional.paymentsDiscounts.paymentMethods.post(connection, {
        user_id: userA,
        type: "MasterCard",
        masked_detail: "**** **** **** 5678",
        provider: null,
        active: false,
    });
    typia.assert<IPaymentMethod>(pmA_inactive);
    const pmB_active = await api.functional.paymentsDiscounts.paymentMethods.post(connection, {
        user_id: userB,
        type: "Wallet",
        masked_detail: "0xDEADBEEF",
        provider: "Binance",
        active: true,
    });
    typia.assert<IPaymentMethod>(pmB_active);
    const pmB_inactive = await api.functional.paymentsDiscounts.paymentMethods.post(connection, {
        user_id: userB,
        type: "VISA",
        masked_detail: "**** **** **** 9876",
        provider: "Toss",
        active: false,
    });
    typia.assert<IPaymentMethod>(pmB_inactive);

    // Helper to test filtering logic
    const fetch = async (filter: Partial<IPaymentMethod>) => {
        const req = {
            user_id: filter.user_id || null,
            type: filter.type || null,
            provider: filter.provider === undefined ? null : filter.provider,
            active: typeof filter.active === "boolean" ? filter.active : null,
        };
        const pageRes = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, req);
        typia.assert<IPageIPaymentMethod>(pageRes);
        return pageRes;
    };
    // 3. Search for userA's payment methods
    const listA = await fetch({ user_id: userA });
    TestValidator.equals("userA list length")([pmA_active, pmA_inactive].length)(listA.data.length);
    TestValidator.predicate("userA only pmA records")(() => listA.data.every(m => m.user_id === userA));
    // 4. Search for active VISA for userB (should be 0)
    const visaActiveB = await fetch({ user_id: userB, type: "VISA", active: true });
    TestValidator.equals("userB VISA active empty")(0)(visaActiveB.data.length);
    // 5. Search for all active methods (should find pmA_active, pmB_active)
    const activeAll = await fetch({ active: true });
    const ids = activeAll.data.map(x => x.id);
    TestValidator.predicate("active methods match created")(() => ids.includes(pmA_active.id) && ids.includes(pmB_active.id) && ids.length === 2);
    // 6. Pagination: get first page, limit 1
    const paged = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
        page: 1,
        limit: 1,
        sort: "created_at:asc",
    });
    typia.assert<IPageIPaymentMethod>(paged);
    TestValidator.equals("limit 1 length")(1)(paged.data.length);
    TestValidator.equals("pagination meta")({ current: 1, limit: 1 })(
        { current: paged.pagination.current, limit: paged.pagination.limit }
    );
    // 7. Out-of-range page
    const emptyPage = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
        page: 999,
        limit: 5,
    });
    typia.assert<IPageIPaymentMethod>(emptyPage);
    TestValidator.equals("empty page length")(0)(emptyPage.data.length);
    // 8. Invalid user_id (should yield no results, not error)
    const invalidUser = await fetch({ user_id: "00000000-0000-0000-0000-000000000000" });
    TestValidator.equals("invalid user_id yields empty")(0)(invalidUser.data.length);
    // 9. Search for method with non-matching provider name
    const nopeProvider = await fetch({ provider: "NonexistentProvider" });
    TestValidator.equals("no provider match")(0)(nopeProvider.data.length);
    // 10. Search for all as admin (default/no filter): should see all 4
    const all = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {});
    typia.assert<IPageIPaymentMethod>(all);
    TestValidator.equals("admin/all four")([pmA_active, pmA_inactive, pmB_active, pmB_inactive].length)(all.data.length);
    TestValidator.predicate("all+visible ids match")(() => {
        const set = new Set(all.data.map(x => x.id));
        return [pmA_active, pmA_inactive, pmB_active, pmB_inactive].every(x => set.has(x.id)) && set.size === 4;
    });
}

