import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPaymentMethod } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentMethod";
import typia from "typia";

export async function test_api_create_payment_method_with_validation_and_duplicate_check(
  connection: api.IConnection,
): Promise<void> {
  // 1. Prepare a valid payment method input
  const validInput: IPaymentMethod.ICreate = {
    user_id: crypto.randomUUID(),
    type: "VISA",
    masked_detail: "411111******1111",
    provider: "Toss",
    active: true,
  };

  // 2. Create new payment method (should succeed)
  const created = await api.functional.paymentsDiscounts.paymentMethods.post(connection, validInput);
  typia.assert<IPaymentMethod>(created);
  TestValidator.equals("returned values consistent")(validInput.type)(created.type);
  TestValidator.equals("returned values consistent")(validInput.masked_detail)(created.masked_detail);
  TestValidator.equals("returned values consistent")(validInput.user_id)(created.user_id);
  TestValidator.equals("must be active")(true)(created.active);
  TestValidator.predicate("created_at should be string")(() => typeof created.created_at === "string");

  // 3. Try to create with missing required fields (should fail - no type)
  const missingType: Partial<IPaymentMethod.ICreate> = {
    user_id: crypto.randomUUID(),
    masked_detail: "1234****5678",
    provider: "Stripe",
    active: true,
  };
  await TestValidator.httpError("missing type triggers 400")(400)(
    () => api.functional.paymentsDiscounts.paymentMethods.post(connection, missingType as any),
  );

  // 4. Try to create with missing masked_detail (should fail)
  const missingMasked: Partial<IPaymentMethod.ICreate> = {
    user_id: crypto.randomUUID(),
    type: "VISA",
    provider: "Stripe",
    active: true,
  };
  await TestValidator.httpError("missing masked_detail triggers 400")(400)(
    () => api.functional.paymentsDiscounts.paymentMethods.post(connection, missingMasked as any),
  );

