): Promise<void> {
  // Step 1: Create diverse discounts
  const ruleActive = "a1111111-1111-4111-8111-111111111111";
  const ruleInactive = "b2222222-2222-4222-8222-222222222222";
  const ruleCampaign = "c3333333-3333-4333-8333-333333333333";
  const orderA = "11111111-aaaa-4aaa-8aaa-111111111111";
  const orderB = "22222222-bbbb-4bbb-8bbb-222222222222";
  const orderC = "33333333-cccc-4ccc-8ccc-333333333333";
  const campaignId = "d4444444-4444-4444-8444-444444444444";

  // Various discount entities
  const discounts: IDiscount[] = [];
  // 1. Active discount
  discounts.push(
    await api.functional.paymentsDiscounts.discounts.post(connection, {
      discount_rule_id: ruleActive,
      order_id: orderA,
      amount: -5000,
      notes: "active discount, ruleActive",
    }),
  );
  // 2. Disabled discount (status: removed) 
  discounts.push(
    await api.functional.paymentsDiscounts.discounts.post(connection, {
      discount_rule_id: ruleInactive,
      order_id: orderB,
      amount: -1000,
      notes: "removed discount, marked as disabled",
    }),
  );
  // 3. Campaign-linked discount
  discounts.push(
    await api.functional.paymentsDiscounts.discounts.post(connection, {
      discount_rule_id: ruleCampaign,
      order_id: orderC,
      amount: -1500,
      notes: "campaign discount",
    }),
  );

  // Step 2: Search for active discounts (simulate status filter)
  const reqActive = { status: "active" };
  const resActive = await api.functional.paymentsDiscounts.discounts.patch(
    connection,
    reqActive,
  );
  typia.assert<IPageIDiscount>(resActive);
  // Confirm all discounts are "active" (simulate: all with ruleActive only)
  for (const d of resActive.data) {
    TestValidator.predicate("discount status active" )(() => d.discount_rule_id === ruleActive);
  }

  // Step 3: Filter by campaign_id
  const reqCampaign = { campaign_id: campaignId };
  const resCampaign = await api.functional.paymentsDiscounts.discounts.patch(
    connection,
    reqCampaign,
  );
  typia.assert<IPageIDiscount>(resCampaign);
  for (const d of resCampaign.data) {
    TestValidator.predicate("campaign id match")(() => d.discount_rule_id === ruleCampaign);
  }

  // Step 4: Pagination limit
  const reqPaginate = { limit: 1 };
  const resPaginated = await api.functional.paymentsDiscounts.discounts.patch(
    connection,
    reqPaginate,
  );
