  TestValidator.predicate("pagination limited to 1")(() => resPaginated.data.length <= 1);
  TestValidator.predicate("pagination meta exists")(() => typeof resPaginated.pagination.current === "number");

  // Step 5: Edge case, filter for non-existent rule
  const reqNonExist = { discount_rule_id: "99999999-9999-4999-8999-999999999999" };
  const resNonExist = await api.functional.paymentsDiscounts.discounts.patch(
    connection,
    reqNonExist,
  );
  typia.assert<IPageIDiscount>(resNonExist);
  TestValidator.equals("empty discount array")([] as IDiscount[])(resNonExist.data);

  // Step 6: Invalid input (page as string, limit negative)
  await TestValidator.error("invalid page/limit should error")(
    async () =>
      await api.functional.paymentsDiscounts.discounts.patch(connection, {
        page: "bad" as any,
        limit: -5 as any,
      }),
  );
}

