export async function test_list_users_after_creating_and_deleting_users(connection: api.IConnection): Promise<void> {
  // Step 1: Prepare new users with unique data
  const userPayloads: IUser.ICreate[] = [
    {
      organization_id: "22222222-2222-2222-2222-222222222222",
      role_id: "33333333-3333-3333-3333-333333333331",
      username: `testuser_a_${Date.now()}`,
      email: `user_a_${Date.now()}@example.com`,
      password: "Passw0rd!A",
      phone: "111-1111-1111"
    },
    {
      organization_id: "22222222-2222-2222-2222-222222222222",
      role_id: "33333333-3333-3333-3333-333333333332",
      username: `testuser_b_${Date.now()}`,
      email: `user_b_${Date.now()}@example.com`,
      password: "Passw0rd!B",
      phone: "222-2222-2222"
    },
    {
      organization_id: "22222222-2222-2222-2222-222222222222",
      role_id: "33333333-3333-3333-3333-333333333333",
      username: `testuser_c_${Date.now()}`,
      email: `user_c_${Date.now()}@example.com`,
      password: "Passw0rd!C"
      // phone is optional, omit
    }
  ];

  // Step 2: Create the users
  const createdUsers: IUser[] = [];
  for (const payload of userPayloads) {
    const created = await api.functional.core.users.post(connection, payload);
    typia.assert<IUser>(created);
    createdUsers.push(created);
  }

  // Step 3: List all users (optionally, filter by org for cleaner test)
  const listRequest: IUser.IRequest = {
    organization_id: "22222222-2222-2222-2222-222222222222"
  };
  const pageResult = await api.functional.core.users.patch(connection, listRequest);
  typia.assert<{ pagination: any; data: IUser[] }>(pageResult);

  // Step 4: Validate that all created users are present (use username/email match)
  for (const created of createdUsers) {
    const found = pageResult.data.find(u => u.username === created.username && u.email === created.email);
    TestValidator.predicate(`Created user ${created.username} should appear in user list`)(!!found);
    if (found) {
      // Validate all essential fields (allow updated_at drift)
      TestValidator.equals("id")(created.id)(found.id);
      TestValidator.equals("username")(created.username)(found.username);
      TestValidator.equals("email")(created.email)(found.email);
      TestValidator.equals("organization_id")(created.organization_id)(found.organization_id);
