import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import typia from "typia";

export async function test_create_user_with_valid_invalid_and_duplicate_data(connection: api.IConnection): Promise<void> {
    // ----- 1. Positive Case: Create a new user with all fields -----
    const uniqueId = `${Date.now()}-${Math.floor(Math.random() * 10000)}`;
    const validUser: IUser.ICreate = {
        organization_id: "11111111-2222-3333-4444-555555555555",
        role_id: "aaaaaaaa-bbbb-cccc-dddd-eeeeeeeeeeee",
        username: `testuser_${uniqueId}`,
        email: `test+${uniqueId}@example.com`,
        phone: "+82200001111",
        password: "secureP@ssw0rd#${uniqueId}",
    };
    const created = await api.functional.core.users.post(connection, validUser);
    typia.assert<IUser>(created);
    TestValidator.equals("username matches")(validUser.username)(created.username);
    TestValidator.equals("email matches")(validUser.email)(created.email);
    TestValidator.equals("org_id matches")(validUser.organization_id)(created.organization_id);
    TestValidator.equals("role_id matches")(validUser.role_id)(created.role_id);
    TestValidator.predicate("has user id")(!!created.id && typeof created.id === "string" && created.id.length > 0);
    TestValidator.predicate("created_at exists")(!!created.created_at);
    TestValidator.predicate("updated_at exists")(!!created.updated_at);
    TestValidator.predicate("is_active exists")(typeof created.is_active === "boolean");

    // ----- 2. Negative Cases: Missing each required field -----
    const requireds: (keyof IUser.ICreate)[] = ["role_id", "username", "email", "password"];
    for (const key of requireds) {
        const base: IUser.ICreate = { ...validUser };
        delete base[key];
        // For password, can't have undefined (API may expect missing)
        // Use as any to simulate missing prop
        await TestValidator.httpError(`Validation error when omitted ${key}`)(400)(
            () => api.functional.core.users.post(connection, base as any)
        );
    }

    // ----- 3. Negative Cases: Duplicate username and email -----
    // Attempt to create user using the same username
    const dupeUsernameUser: IUser.ICreate = {
        ...validUser,
        email: `diffemail_${uniqueId}@example.com`, // ensure email is unique
        password: "AnotherP@ss1",
    };
    await TestValidator.httpError("Duplicate username not allowed")(409, 400)(
        () => api.functional.core.users.post(connection, dupeUsernameUser)
    );

    // Attempt to create user using the same email
    const dupeEmailUser: IUser.ICreate = {
        ...validUser,
        username: `diffuser_${uniqueId}`,
        password: "DiffP@ss2",
    };
    await TestValidator.httpError("Duplicate email not allowed")(409, 400)(
        () => api.functional.core.users.post(connection, dupeEmailUser)
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import typia from "typia";

export async function test_update_user_with_valid_invalid_and_nonexistent_id(connection: api.IConnection): Promise<void> {
    // 1. Create a valid user
    const createInput: IUser.ICreate = {
        organization_id: "7be337e2-8dd2-11ee-b9d1-0242ac120002",
        role_id: "a81474d5-9288-4269-9ccb-03c890ff0b05",
        username: "update_test_user_" + Math.floor(Math.random() * 100000),
        email: `updatetestuser${Math.floor(Math.random() * 100000)}@test.com`,
        password: "SecureP@ssword1123!",
        phone: "555-418-2399",
    };
    const user = await api.functional.core.users.post(connection, createInput);
    typia.assert<IUser>(user);

    // 2. Update legitimate fields (email, username)
    const updateInput: IUser.IUpdate = {
        email: `updated_${Date.now()}@example.com`,
        username: "new_username_" + Math.floor(Math.random() * 100000),
    };
    const updated = await api.functional.core.users.put(connection, user.id, updateInput);
    typia.assert<IUser>(updated);
    TestValidator.equals("should update email")(updateInput.email!)(updated.email);
    TestValidator.equals("should update username")(updateInput.username!)(updated.username);
    TestValidator.equals("should maintain original id")(user.id)(updated.id);

    // 3. Try invalid email update
    const invalidUpdate: IUser.IUpdate = {
        email: "not-an-email",
    };
    await TestValidator.httpError("Invalid email format should cause 400", 400)(() =>
        api.functional.core.users.put(connection, user.id, invalidUpdate)
    );

    // 4. Try non-existent user update
    const randomNonexistentId = "e7d01124-b0ad-4dbe-bb16-ada158c9e0ab";
    await TestValidator.httpError("Updating non-existent user should 404", 404)(() =>
        api.functional.core.users.put(connection, randomNonexistentId, updateInput)
    );

    // 5. Try empty update (should either be a no-op or an error)
    const emptyUpdate: IUser.IUpdate = {};
    const maybeNoOpOrError = await TestValidator.proceed(() =>
        api.functional.core.users.put(connection, user.id, emptyUpdate)
    );
    if (!maybeNoOpOrError) {
        // If it does not error, the values must remain identical
        const userAfterNoop = await api.functional.core.users.put(connection, user.id, {});
        typia.assert<IUser>(userAfterNoop);
        TestValidator.equals("should not change email after empty update")(updated.email)(userAfterNoop.email);
        TestValidator.equals("should not change username after empty update")(updated.username)(userAfterNoop.username);
    }
}

