import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUserProfile } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserProfile";
import typia from "typia";

export async function test_update_user_profile_with_audit_and_error_cases(connection: api.IConnection): Promise<void> {
  // 1. Create a user profile for update testing
  const userId = crypto.randomUUID();
  const initialProfileInput: IUserProfile.ICreate = {
    user_id: userId,
    locale_id: crypto.randomUUID(),
    full_name: "Jane Doe",
    avatar_url: "https://cdn.example.com/avatar/jane.png",
    timezone: "Asia/Seoul",
    bio: "Initial bio for update test",
    a11y_prefs: "{\"contrast\":\"high\"}"
  };
  const created = await api.functional.core.userProfiles.post(connection, initialProfileInput);
  typia.assert<IUserProfile>(created);
  TestValidator.equals("full_name after create")("Jane Doe")(created.full_name!);
  const profileId = created.id;

  // 2. Valid update of metadata fields
  const updatedInput: IUserProfile.IUpdate = {
    full_name: "Jane Delta",
    bio: "Updated bio content.",
    timezone: "Europe/Berlin",
    a11y_prefs: "{\"contrast\":\"low\",\"font\":\"large\"}"
  };
  const updated = await api.functional.core.userProfiles.put(connection, profileId, updatedInput);
  typia.assert<IUserProfile>(updated);
  TestValidator.equals("updated id is same as created") (created.id) (updated.id);
  TestValidator.equals("updated user_id is same") (created.user_id) (updated.user_id);
  TestValidator.equals("full_name updated") (updatedInput.full_name!) (updated.full_name!);
  TestValidator.equals("bio updated") (updatedInput.bio!) (updated.bio!);
  TestValidator.equals("timezone updated") (updatedInput.timezone!) (updated.timezone!);
  TestValidator.equals("a11y_prefs updated") (updatedInput.a11y_prefs!) (updated.a11y_prefs!);
  TestValidator.predicate("updated_at should differ from created_at") (() => updated.updated_at !== updated.created_at);
  TestValidator.equals("created_at unchanged") (created.created_at) (updated.created_at);

  // 3. Attempt forbidden/immutable field change - user_id/id cannot be updated, should error
  const forbiddenUpdate: IUserProfile.IUpdate = { full_name: "Invalid", } as any;
  Object.assign(forbiddenUpdate, { id: crypto.randomUUID(), user_id: crypto.randomUUID() });
  await TestValidator.httpError("immutable fields update should fail")(400, 403, 422)(
    () => api.functional.core.userProfiles.put(connection, profileId, forbiddenUpdate)
  );

  // 4. Attempt to update non-existent profile
  const nonExistingId = crypto.randomUUID();
  await TestValidator.httpError("update on non-existent profile should fail")(404)(
    () => api.functional.core.userProfiles.put(connection, nonExistingId, updatedInput)
  );
}

