    const orgAInput: IOrganization.ICreate = {
        name: `TestOrgA_${Date.now()}`,
        code: `testorg_a_${Date.now()}`,
    };
    const orgBInput: IOrganization.ICreate = {
        name: `TestOrgB_${Date.now()}`,
        code: `testorg_b_${Date.now()}`,
    };

    const orgA = await api.functional.core.organizations.post(connection, orgAInput);
    const orgB = await api.functional.core.organizations.post(connection, orgBInput);
    typia.assert<IOrganization>(orgA);
    typia.assert<IOrganization>(orgB);
    TestValidator.equals("orgA name")(orgAInput.name)(orgA.name);
    TestValidator.equals("orgB name")(orgBInput.name)(orgB.name);
    TestValidator.equals("orgA code")(orgAInput.code)(orgA.code);
    TestValidator.equals("orgB code")(orgBInput.code)(orgB.code);

    // 2. List with no filters, expect both present
    const withoutFilter: IOrganization.IRequest = { page: 1, limit: 10 };
    const listAll = await api.functional.core.organizations.patch(connection, withoutFilter);
    typia.assert<IPageIOrganization>(listAll);
    const foundA = listAll.data.some(o => o.id === orgA.id);
    const foundB = listAll.data.some(o => o.id === orgB.id);
    TestValidator.equals("Should contain orgA")(true)(foundA);
    TestValidator.equals("Should contain orgB")(true)(foundB);
    TestValidator.predicate("pagination structure")(() => typeof listAll.pagination === "object");

    // 3. List with name filter (orgA)
    const filterByName: IOrganization.IRequest = { page: 1, limit: 10, name: orgAInput.name };
    const listByName = await api.functional.core.organizations.patch(connection, filterByName);
    typia.assert<IPageIOrganization>(listByName);
    TestValidator.equals("Filter by name returns 1")(1)(listByName.data.length);
    TestValidator.equals("Name matches")(orgAInput.name)(listByName.data[0]?.name);

    // 4. List with code filter (orgB)
    const filterByCode: IOrganization.IRequest = { page: 1, limit: 10, code: orgBInput.code };
    const listByCode = await api.functional.core.organizations.patch(connection, filterByCode);
    typia.assert<IPageIOrganization>(listByCode);
    TestValidator.equals("Filter by code returns 1")(1)(listByCode.data.length);
    TestValidator.equals("Code matches")(orgBInput.code)(listByCode.data[0]?.code);

