import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IRole";
import { IPageIRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIRole";
import typia from "typia";

export async function test_list_roles_with_pagination_and_filtering(connection: api.IConnection): Promise<void> {
    // 1. Prerequisite: create roles for two organizations
    const orgId1 = "11111111-1111-1111-1111-111111111111";
    const orgId2 = "22222222-2222-2222-2222-222222222222";
    const roleA = await api.functional.core.roles.post(connection, {
        organization_id: orgId1,
        name: "admin",
        code: "ADMIN",
        description: "Org1 Admin role"
    });
    const roleB = await api.functional.core.roles.post(connection, {
        organization_id: orgId1,
        name: "customer",
        code: "CUSTOMER",
        description: "Org1 Customer role"
    });
    const roleC = await api.functional.core.roles.post(connection, {
        organization_id: orgId2,
        name: "manager",
        code: "MANAGER",
        description: "Org2 Manager role"
    });

    // 2a. Retrieve all roles (no filters)
    const resAll = await api.functional.core.roles.patch(connection, { page: 1, limit: 10 });
    typia.assert<IPageIRole>(resAll);
    TestValidator.predicate("should include all created roles")(() =>
        [roleA.id, roleB.id, roleC.id].every(id => resAll.data.some(r => r.id === id))
    );

    // 2b. Filter by organization_id (Org1)
    const resOrg1 = await api.functional.core.roles.patch(connection, { organization_id: orgId1 });
    typia.assert<IPageIRole>(resOrg1);
    TestValidator.predicate("Org1 filter returns only Org1 roles")(() =>
        resOrg1.data.every(r => r.organization_id === orgId1) &&
        [roleA.id, roleB.id].every(id => resOrg1.data.some(r => r.id === id))
    );
    TestValidator.predicate("Org2 roles not in Org1 results")(() =>
        resOrg1.data.every(r => r.id !== roleC.id)
    );

    // 2c. Filter by name (partial matches)
    const resAdmin = await api.functional.core.roles.patch(connection, { name: "admin" });
    typia.assert<IPageIRole>(resAdmin);
    TestValidator.predicate("filter by name=admin finds only admin role")(() =>
        resAdmin.data.length >= 1 && resAdmin.data.some(r => r.name === "admin")
    );
    TestValidator.predicate("all roles in result have correct name")(() =>
        resAdmin.data.every(r => r.name.toLowerCase().includes("admin"))
    );
    // 2c. Filter by code
    const resCode = await api.functional.core.roles.patch(connection, { code: "CUSTOMER" });
    typia.assert<IPageIRole>(resCode);
    TestValidator.predicate("filter by code=CUSTOMER finds only customer role")(() =>
        resCode.data.length >= 1 && resCode.data.every(r => r.code === "CUSTOMER")
    );

    // 2d. Pagination: page 1, limit 2 (expect at least 2 roles)
    const resPaginated = await api.functional.core.roles.patch(connection, { page: 1, limit: 2 });
    typia.assert<IPageIRole>(resPaginated);
    TestValidator.predicate("paginated result contains at most 2 roles")(() =>
        resPaginated.data.length <= 2 && resPaginated.pagination.limit === 2 && resPaginated.pagination.current === 1
    );

    // 3a. Negative: negative page number
    await TestValidator.httpError("negative page number error")(400)(async () => {
        await api.functional.core.roles.patch(connection, { page: -2, limit: 2 });
    });
    // 3a. Excessive limit
    await TestValidator.httpError("excessive limit error")(400, 422)(async () => {
        await api.functional.core.roles.patch(connection, { page: 1, limit: 10000 });
    });

    // 3b. Non-existent organization_id
    const resNone = await api.functional.core.roles.patch(connection, { organization_id: "00000000-0000-0000-0000-000000000000" });
    typia.assert<IPageIRole>(resNone);
    TestValidator.equals("empty results for nonexistent org")([] as IRole[])(resNone.data);

    // 3b. Non-existent name/code
    const resFakeName = await api.functional.core.roles.patch(connection, { name: "doesnotexist" });
    typia.assert<IPageIRole>(resFakeName);
    TestValidator.equals("empty results for fake name")([] as IRole[])(resFakeName.data);

    const resFakeCode = await api.functional.core.roles.patch(connection, { code: "NO_SUCH_CODE" });
    typia.assert<IPageIRole>(resFakeCode);
    TestValidator.equals("empty results for fake code")([] as IRole[])(resFakeCode.data);
}

