import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IRole";
import typia from "typia";

export async function test_api_update_role_with_valid_invalid_and_nonexistent_id(connection: api.IConnection): Promise<void> {
  // 1. Create a role for subsequent update
  const createInput: IRole.ICreate = {
    organization_id: "11111111-1111-1111-1111-111111111111", // deterministic UUID for org
    name: "Original Role Name",
    code: "original_role_code",
    description: "Role for update tests",
  };
  const created = await api.functional.core.roles.post(connection, createInput);
  typia.assert<IRole>(created);

  // 2. Update the role's name, code, and description
  const updateInput: IRole.IUpdate = {
    name: "Updated Role Name",
    code: "updated_role_code",
    description: "Updated description",
  };
  const updated = await api.functional.core.roles.put(connection, created.id, updateInput);
  typia.assert<IRole>(updated);
  TestValidator.equals("role updated name")(updateInput.name!)(updated.name);
  TestValidator.equals("role updated code")(updateInput.code!)(updated.code);
  TestValidator.equals("role updated description")(updateInput.description!)(updated.description);
  TestValidator.equals("organization id unchanged")(created.organization_id)(updated.organization_id);
  TestValidator.predicate("created_at should remain unchanged")(() => created.created_at === updated.created_at);
  TestValidator.predicate("updated_at should change")(() => created.updated_at !== updated.updated_at);

  // 3. Attempt to update a non-existent role ID
  const nonExistentId = "22222222-2222-2222-2222-222222222222";
  await TestValidator.httpError("update non-existent role")(404)(
    () => api.functional.core.roles.put(connection, nonExistentId, updateInput),
  );

  // 4. Submit invalid update (empty name)
  const invalidUpdateInput: IRole.IUpdate = {
    name: "", // invalid: name cannot be empty
  };
  await TestValidator.httpError("empty name - validation error")(400)(
    () => api.functional.core.roles.put(connection, created.id, invalidUpdateInput),
  );

  // 5. Submit duplicate code (should exist already: updated.code)
  // First, create a second role
  const createInput2: IRole.ICreate = {
    organization_id: createInput.organization_id,
    name: "Second Role",
    code: "second_role_code",
    description: "Second for duplicate code test",
  };
  const created2 = await api.functional.core.roles.post(connection, createInput2);
  typia.assert<IRole>(created2);
  // Attempt to update created2 with duplicate code (now used by updated)
  const duplicateCodeUpdate: IRole.IUpdate = {
    code: updateInput.code, // already assigned to first role
  };
  await TestValidator.httpError("duplicate code - conflict error")(409)(
    () => api.functional.core.roles.put(connection, created2.id, duplicateCodeUpdate),
  );
}

