export async function test_api_list_sessions_with_pagination_and_filtering(connection: api.IConnection): Promise<void> {
  // 1. Prepare test sessions
  const now = new Date();
  const sessions: ISession[] = [];
  for (let i = 0; i < 4; ++i) {
    const input: ISession.ICreate = {
      user_id: `00000000-0000-0000-0000-00000000000${i}`,
      token: `tok-test-${i}`,
      expires_at: new Date(now.getTime() + (i + 1) * 3600 * 1000).toISOString(),
      device_fingerprint: i % 2 === 0 ? `device-${i}` : null,
      ip: i % 2 === 1 ? `192.168.0.${i}` : null
    };
    const created = await api.functional.core.sessions.post(connection, input);
    typia.assert<ISession>(created);
    sessions.push(created);
  }

  // 2. Default pagination (no filters)
  const res1 = await api.functional.core.sessions.patch(connection, {
    page: 1,
    limit: 10
  });
  typia.assert<IPageISession>(res1);
  TestValidator.equals("all data count matches")(sessions.length)(
    res1.data.filter(x => sessions.some(s => s.id === x.id)).length
  );

  // 3. Filtering by user_id
  const filterUser = sessions[1].user_id;
  const res2 = await api.functional.core.sessions.patch(connection, {
    user_id: filterUser,
    page: 1,
    limit: 10
  });
  typia.assert<IPageISession>(res2);
  TestValidator.equals("filter by user_id")(
    sessions.filter(s => s.user_id === filterUser).map(s => s.id).sort()
  )(
    res2.data.map(s => s.id).filter(id => sessions.some(ss => ss.id === id)).sort()
  );

  // 4. Pagination (page 2 with limit 2)
  const res3 = await api.functional.core.sessions.patch(connection, {
    page: 2,
    limit: 2
  });
  typia.assert<IPageISession>(res3);
  TestValidator.equals("pagination meta page/limit")({ current: 2, limit: 2 })(
    { current: res3.pagination.current, limit: res3.pagination.limit }
  );

  // 5. Filtering by device_fingerprint
  const df = sessions.find(s => typeof s.device_fingerprint === "string")?.device_fingerprint || "device-0";
  const res4 = await api.functional.core.sessions.patch(connection, {
    device_fingerprint: df,
    page: 1,
    limit: 10
  });
  typia.assert<IPageISession>(res4);
  TestValidator.equals("filter by device_fingerprint")(
    sessions.filter(s => s.device_fingerprint === df).map(s => s.id).sort()
  )(
    res4.data.map(s => s.id).filter(id => sessions.some(ss => ss.id === id)).sort()
  );

  // 6. Invalid: negative page/limit
  await TestValidator.httpError("negative page/limit should fail")(400)(async () => {
    await api.functional.core.sessions.patch(connection, {
      page: -1,
      limit: 0
    });
  });

  // 7. Invalid: malformed expires_before
  await TestValidator.httpError("bad date string should fail")(400)(async () => {
    await api.functional.core.sessions.patch(connection, {
      expires_before: "not-a-date"
    });
  });
}

