export async function test_api_create_session_with_valid_invalid_and_duplicate_data(connection: api.IConnection): Promise<void> {
  // Step 1: Create valid session data
  const sessionInput: ISession.ICreate = {
    user_id: "cda64339-dfbe-45ec-8a5d-bf6eed8efed9",
    token: "test-session-token-1234",
    expires_at: new Date(Date.now() + 1000 * 60 * 60).toISOString(), // 1 hour later
    device_fingerprint: "device-fingerprint-7890",
    ip: "203.0.113.100",
  };

  // Step 2: Create session (happy path)
  const result = await api.functional.core.sessions.post(connection, sessionInput);
  typia.assert<ISession>(result);
  TestValidator.equals("user_id matches")(sessionInput.user_id)(result.user_id);
  TestValidator.equals("token matches")(sessionInput.token)(result.token);
  TestValidator.equals("expires_at matches")(sessionInput.expires_at)(result.expires_at);
  TestValidator.equals("device_fingerprint matches")(sessionInput.device_fingerprint)(result.device_fingerprint);
  TestValidator.equals("ip matches")(sessionInput.ip)(result.ip);
  TestValidator.predicate("Has uuid id")(() => /^\w{8}-(\w{4}-){3}\w{12}$/.test(result.id));
  TestValidator.predicate("Has created_at")(() => typeof result.created_at === "string");
  TestValidator.equals("revoked_at is null")(null)(result.revoked_at);

  // Step 3: Invalid session creation (missing user_id)
  await TestValidator.httpError("Missing user_id triggers 400")(400)(async () => {
    const input: ISession.ICreate = {
      // user_id missing
      token: "token-no-userid",
      expires_at: new Date(Date.now() + 1000 * 60 * 60 * 5).toISOString(),
      device_fingerprint: null,
      ip: null,
    } as any;
    await api.functional.core.sessions.post(connection, input);
  });

  // Step 4: Invalid session creation (missing token)
  await TestValidator.httpError("Missing token triggers 400")(400)(async () => {
    const input: ISession.ICreate = {
      user_id: "cda64339-dfbe-45ec-8a5d-bf6eed8efed9",
      // token missing
      expires_at: new Date(Date.now() + 1000 * 60 * 60 * 5).toISOString(),
      device_fingerprint: null,
      ip: null,
    } as any;
    await api.functional.core.sessions.post(connection, input);
  });

  // Step 5: Invalid session creation (missing expires_at)
  await TestValidator.httpError("Missing expires_at triggers 400")(400)(async () => {
    const input: ISession.ICreate = {
      user_id: "cda64339-dfbe-45ec-8a5d-bf6eed8efed9",
      token: "token-missing-expiry",
      // expires_at missing
      device_fingerprint: null,
      ip: null,
    } as any;
    await api.functional.core.sessions.post(connection, input);
  });

  // Step 6: Try duplicate session token (should fail if uniqueness is enforced)
  await TestValidator.httpError("Duplicate token triggers error")(400, 409)(async () => {
    // Use the same input as the first valid session (same token)
    await api.functional.core.sessions.post(connection, sessionInput);
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ISession } from "@ORGANIZATION/PROJECT-api/lib/structures/ISession";
import typia from "typia";

export async function test_api_update_session_with_valid_invalid_and_nonexistent_id(connection: api.IConnection): Promise<void> {
    // Step 1: Create a session to update
    const sessionCreate: ISession.ICreate = {
        user_id: "cda64339-dfbe-45ec-8a5d-bf6eed8efed9",
        token: `token-${Date.now()}`,
        expires_at: new Date(Date.now() + 3600_000).toISOString(),
        device_fingerprint: "device-123",
        ip: "203.0.113.192",
    };
    const created = await api.functional.core.sessions.post(connection, sessionCreate);
    typia.assert<ISession>(created);
    TestValidator.equals("Create session: user_id")(sessionCreate.user_id)(created.user_id);
    TestValidator.equals("Create session: token")(sessionCreate.token)(created.token);
    TestValidator.equals("Create session: expires_at")(sessionCreate.expires_at)(created.expires_at);

    // Step 2: Valid update - change expiry and device info
    const updatePayload: ISession.IUpdate = {
        expires_at: new Date(Date.now() + 7200_000).toISOString(),
        device_fingerprint: "device-456",
        ip: "203.0.113.200",
    };
    const updated = await api.functional.core.sessions.put(connection, created.id, updatePayload);
    typia.assert<ISession>(updated);
    TestValidator.equals("Update session: expires_at")(updatePayload.expires_at)(updated.expires_at);
    TestValidator.equals("Update session: device_fingerprint")(updatePayload.device_fingerprint)(updated.device_fingerprint);
    TestValidator.equals("Update session: ip")(updatePayload.ip)(updated.ip);

    // Step 3: Invalid update payloads
    // (A) Empty payload - should be valid (no changes)
    await TestValidator.equals("Empty update payload results in no changes")(<ISession.IUpdate>{})(async () => {
        const res = await api.functional.core.sessions.put(connection, created.id, {});
        return {
            expires_at: res.expires_at,
            device_fingerprint: res.device_fingerprint,
            ip: res.ip,
        };
    });

    // (B) Invalid expires_at (bad format)
    await TestValidator.httpError("Invalid expires_at date format")(400)(async () => {
        await api.functional.core.sessions.put(connection, created.id, {
            expires_at: "not-a-date"
        } as ISession.IUpdate);
    });

    // Step 4: Nonexistent ID update
    const invalidId = "11111111-1111-1111-1111-111111111111";
    await TestValidator.httpError("Update with nonexistent session ID")(404)(async () => {
        await api.functional.core.sessions.put(connection, invalidId, updatePayload);
    });
}

