import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ICoreLocale } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreLocale";
import { IPageICoreLocale } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICoreLocale";

export async function test_api_list_locales_with_pagination_and_filtering(connection: api.IConnection): Promise<void> {
    // Step 1: Create locales
    const localesToCreate: ICoreLocale.ICreate[] = [
        { code: "en-US", name: "English (United States)" },
        { code: "ko-KR", name: "Korean (South Korea)" },
        { code: "ja-JP", name: "Japanese (Japan)" },
    ];
    const createdLocales: ICoreLocale[] = [];
    for (const input of localesToCreate) {
        const result = await api.functional.core.locales.post(connection, input);
        typia.assert<ICoreLocale>(result);
        createdLocales.push(result);
    }

    // Step 2: Fetch all locales (no filter, no pagination)
    const allRes = await api.functional.core.locales.patch(connection, {});
    typia.assert<IPageICoreLocale>(allRes);
    const allCodes = createdLocales.map(l => l.code);
    for (const code of allCodes) {
        TestValidator.predicate(`All locales should be present: ${code}`)(
            allRes.data.some(l => l.code === code)
        );
    }

    // Step 3: Pagination test (limit 2)
    const paged1 = await api.functional.core.locales.patch(connection, { limit: 2, page: 1 });
    typia.assert<IPageICoreLocale>(paged1);
    TestValidator.predicate("Should return 2 items on page 1")(paged1.data.length === 2);
    TestValidator.predicate("Total records >= created count")(paged1.pagination.records >= createdLocales.length);

    const paged2 = await api.functional.core.locales.patch(connection, { limit: 2, page: 2 });
    typia.assert<IPageICoreLocale>(paged2);
    TestValidator.predicate("Should return remaining items or less on page 2")(paged2.data.length <= 2);

    // Step 4: Filtering by code
    const filterCode = localesToCreate[0].code;
    const filtered = await api.functional.core.locales.patch(connection, { filter: { code: filterCode } });
    typia.assert<IPageICoreLocale>(filtered);
    TestValidator.predicate("Only the filtered code should be present")(
        filtered.data.length === 1 && filtered.data[0].code === filterCode
    );

    // Step 5a: Invalid pagination (limit=0, page=-1) should error
    await TestValidator.httpError("Invalid limit and page should error")(400)(async () => {
        await api.functional.core.locales.patch(connection, { limit: 0, page: -1 });
    });

    // Step 5b: Invalid filter property (nonexistent field in filter)
    // API must ignore unknown filter or return empty, but must not crash
    const weirdFilter = await api.functional.core.locales.patch(connection, { filter: { bogusfield: "nope" } });
    typia.assert<IPageICoreLocale>(weirdFilter);
    // Expect either empty or at least not an error
}

