import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IReviewMedia } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewMedia";
import typia from "typia";

export async function test_update_review_media_metadata_with_various_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid review media entry (happy path for update)
  const createInput: IReviewMedia.ICreate = {
    review_id: "11111111-1111-1111-1111-111111111111", // presumed valid UUID for an existing review
    media_type: "image",
    uri: "https://example.com/image1.jpg",
    alt_text: "original alt",
    sequence: 1,
  };
  const createdMedia = await api.functional.communicationReview.reviewMedia.post(connection, createInput);
  typia.assert<IReviewMedia>(createdMedia);

  // 2a. Update alt_text and sequence (happy path)
  const updateInput: IReviewMedia.IUpdate = {
    alt_text: "updated alt-text",
    sequence: 2,
  };
  const updated = await api.functional.communicationReview.reviewMedia.put(
    connection,
    createdMedia.id,
    updateInput,
  );
  typia.assert<IReviewMedia>(updated);
  TestValidator.equals("alt_text updated")<string | null>(updateInput.alt_text)(updated.alt_text);
  TestValidator.equals("sequence updated")<number>(updateInput.sequence)(updated.sequence);
  // Ensure other fields stay intact
  TestValidator.equals("review_id unchanged")<string>(createdMedia.review_id)(updated.review_id);
  TestValidator.equals("media_type unchanged")<string>(createdMedia.media_type)(updated.media_type);
  TestValidator.equals("uri unchanged")<string>(createdMedia.uri)(updated.uri);

  // 2b. Attempt forbidden/immutable update (e.g., uri -- should fail)
  await TestValidator.httpError("uri immutability enforced")(400, 422, 403)(async () =>
    api.functional.communicationReview.reviewMedia.put(
      connection,
      createdMedia.id,
      // @ts-expect-error -- Simulate malicious/invalid update
      { uri: "https://malicious.com/evil.jpg" },
    ),
  );

  // 3. Update with non-existent/fake ID (expect 404)
  const fakeId = "00000000-0000-0000-0000-000000000000";
  await TestValidator.httpError("404 not found for non-existent ID")(404)(() =>
    api.functional.communicationReview.reviewMedia.put(connection, fakeId, updateInput),
  );

  // 4a. Field type error: wrong type for sequence
  const invalidUpdateA = { alt_text: "bad", sequence: "not_a_number" };
  await TestValidator.httpError("type error: sequence must be int")(400, 422)(() =>
    // @ts-expect-error
    api.functional.communicationReview.reviewMedia.put(connection, createdMedia.id, invalidUpdateA),
  );

  // 4b. Field type error: wrong type for alt_text
  const invalidUpdateB = { alt_text: 12345, sequence: 3 };
  await TestValidator.httpError("type error: alt_text must be string or null")(400, 422)(() =>
    // @ts-expect-error
    api.functional.communicationReview.reviewMedia.put(connection, createdMedia.id, invalidUpdateB),
  );

