import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IReviewModeration } from "@ORGANIZATION/PROJECT-api/lib/structures/IReviewModeration";
import typia from "typia";

export async function test_api_review_moderation_list_review_moderations_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // 1. Prepare test data: create 3 moderation records with distinct values
    const baseId = "123e4567-e89b-12d3-a456-4d000000000";
    const reviewIds = [
        `${baseId}1`,
        `${baseId}2`,
        `${baseId}3`,
    ];
    const moderatorIds = [
        `${baseId}a`,
        `${baseId}b`,
        `${baseId}c`,
    ];
    const now = new Date();
    const moderations: IReviewModeration[] = [];
    for (let i = 0; i < 3; ++i) {
        const create: IReviewModeration.ICreate = {
            review_id: reviewIds[i],
            moderator_user_id: moderatorIds[i],
            action_type: i % 2 === 0 ? "flag" : "remove",
            rationale: `Test rationale ${i}`,
        };
        const created = await api.functional.communicationReview.reviewModerations.post(connection, create);
        typia.assert<IReviewModeration>(created);
        moderations.push(created);
    }
    // 2. List all review moderations, unfiltered
    const unfilteredReq: IReviewModeration.IRequest = {};
    const allList = await api.functional.communicationReview.reviewModerations.patch(connection, unfilteredReq);
    typia.assert(allList);
    // All created moderations must be in the results
    for (const m of moderations) {
        TestValidator.predicate(`moderation ${m.id} found in unfiltered list`)(() =>
            !!allList.data.find((x) => x.id === m.id)
        );
    }
    // 3. Apply filters
    // a. Filter by moderator_user_id
    for (let i = 0; i < moderations.length; ++i) {
        const filterReq: IReviewModeration.IRequest = {
            moderator_user_id: moderatorIds[i],
        };
        const filtered = await api.functional.communicationReview.reviewModerations.patch(connection, filterReq);
        typia.assert(filtered);
        TestValidator.equals("filtered count matches", undefined)(1)(filtered.data.length);
        TestValidator.equals("moderator id match", undefined)(moderatorIds[i])(filtered.data[0].moderator_user_id);
    }
    // b. Filter by review_id
    for (let i = 0; i < moderations.length; ++i) {
        const filterReq: IReviewModeration.IRequest = {
            review_id: reviewIds[i],
        };
        const filtered = await api.functional.communicationReview.reviewModerations.patch(connection, filterReq);
        typia.assert(filtered);
        TestValidator.equals("filtered count matches", undefined)(1)(filtered.data.length);
        TestValidator.equals("review id match", undefined)(reviewIds[i])(filtered.data[0].review_id);
    }
    // c. Filter by action_type
    ["flag", "remove"].forEach(async (actionType) => {
        const filterReq: IReviewModeration.IRequest = { action_type: actionType };
        const filtered = await api.functional.communicationReview.reviewModerations.patch(connection, filterReq);
        typia.assert(filtered);
        const expectCount = moderations.filter(m => m.action_type === actionType).length;
        TestValidator.equals(`filtered by action_type = ${actionType} count`, undefined)(expectCount)(filtered.data.length);
        for (const item of filtered.data)
            TestValidator.equals("action_type matches", undefined)(actionType)(item.action_type);
    });
    // d. Filter by created_at_range (narrow window for one entry)
    const target = moderations[0];
    const from = new Date(new Date(target.created_at).getTime() - 1000).toISOString();
    const to = new Date(new Date(target.created_at).getTime() + 1000).toISOString();
    const rangeReq: IReviewModeration.IRequest = {
        created_at_range: { from, to },
    };
    const rangeResult = await api.functional.communicationReview.reviewModerations.patch(connection, rangeReq);
    typia.assert(rangeResult);
    // Only 1 moderations record should be found in this tight window
    TestValidator.equals("created_at_range filter single", undefined)(1)(rangeResult.data.length);
    TestValidator.equals("created_at matches", undefined)(target.id)(rangeResult.data[0].id);
    // 4. Filter: non-existing moderator_user_id (should return empty array)
    const emptyModeratorReq: IReviewModeration.IRequest = { moderator_user_id: baseId + "zz" };
    const emptyRes = await api.functional.communicationReview.reviewModerations.patch(connection, emptyModeratorReq);
    typia.assert(emptyRes);
    TestValidator.equals("non-existing moderator filter yields no results", undefined)(0)(emptyRes.data.length);
    // 5. Pagination: request page beyond last page
    const paginationReq: IReviewModeration.IRequest = { ...unfilteredReq };
    // set limit to 1 so we will have at least 3 pages. Request page 99
    (paginationReq as any).limit = 1;
    (paginationReq as any).current = 99;
    const paged = await api.functional.communicationReview.reviewModerations.patch(connection, paginationReq);
    typia.assert(paged);
    TestValidator.equals("page 99 empty data", undefined)([] as IReviewModeration[])(paged.data);
    TestValidator.equals("pagination, correct page", undefined)(99)(paged.pagination.current);
}

