import typia from "typia";

export async function test_list_review_sentiments_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Prepare multiple review sentiment records for robust filter/pagination tests
  const baseTimestamp = new Date().toISOString();
  const inputs: IReviewSentiment.ICreate[] = [
    {
      review_id: "00000000-0000-0000-0000-000000000001",
      ai_model_version_id: "10000000-0000-0000-0000-000000000010",
      sentiment: "positive",
      score: 0.95,
      analyzed_at: baseTimestamp,
    },
    {
      review_id: "00000000-0000-0000-0000-000000000002",
      ai_model_version_id: "10000000-0000-0000-0000-000000000011",
      sentiment: "negative",
      score: 0.10,
      analyzed_at: baseTimestamp,
    },
    {
      review_id: "00000000-0000-0000-0000-000000000003",
      ai_model_version_id: "10000000-0000-0000-0000-000000000012",
      sentiment: "neutral",
      score: 0.65,
      analyzed_at: baseTimestamp,
    },
  ];
  const created: IReviewSentiment[] = [];
  for (const input of inputs) {
    const rec = await api.functional.communicationReview.reviewSentiments.post(
      connection,
      input,
    );
    typia.assert<IReviewSentiment>(rec);
    created.push(rec);
  }

  // 2a. Query all review sentiments with no filters (should retrieve all or paginated)
  const allRes = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    {},
  );
  typia.assert<IPageIReviewSentiment>(allRes);
  TestValidator.equals("data length matches entries created or pagination limit")(
    allRes.data.length >= created.length,
  )(true);
  TestValidator.predicate("pagination page=1")(
    () => allRes.pagination.current === 1,
  );
  TestValidator.predicate("pagination limit positive")(
    () => allRes.pagination.limit > 0,
  );

  // 2b. Query with tight limit (pagination)
  const limitRes = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    { limit: 2, page: 1 },
  );
  typia.assert<IPageIReviewSentiment>(limitRes);
  TestValidator.equals("should return at most 2 entries")(
    limitRes.data.length <= 2,
  )(true);
  TestValidator.equals("pagination limit=2")(
    limitRes.pagination.limit,
  )(2);

  // 2c1. Filter by review_id
  const filterReview = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    { review_id: created[0].review_id },
  );
  typia.assert<IPageIReviewSentiment>(filterReview);
  TestValidator.equals("filter by review_id returns only matching record")(
    filterReview.data.length,
  )(1);
  TestValidator.equals("check correct review_id")(
    filterReview.data[0].review_id,
  )(created[0].review_id);

  // 2c2. Filter by ai_model_version_id and sentiment
  const filterModelSentiment = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    {
      ai_model_version_id: created[1].ai_model_version_id,
      sentiment: created[1].sentiment,
    },
  );
  typia.assert<IPageIReviewSentiment>(filterModelSentiment);
  TestValidator.equals("filter returns correct entry")(
    filterModelSentiment.data.length,
  )(1);
  TestValidator.equals("ai_model_version_id match")(
    filterModelSentiment.data[0].ai_model_version_id,
  )(created[1].ai_model_version_id);
  TestValidator.equals("sentiment match")(
    filterModelSentiment.data[0].sentiment,
  )(created[1].sentiment);

  // 2c3. Filter by score_min & score_max
  const scoreFilter = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    { score_min: 0.9, score_max: 1.0 },
  );
  typia.assert<IPageIReviewSentiment>(scoreFilter);
  TestValidator.predicate("score filter returns >=1 if present")(
    () => scoreFilter.data.every((e) => e.score >= 0.9 && e.score <= 1.0),
  );

  // 2c4. Filter by analyzed_at window (filter for only present records)
  const timeFilter = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    { analyzed_at_from: baseTimestamp, analyzed_at_to: baseTimestamp },
  );
  typia.assert<IPageIReviewSentiment>(timeFilter);
  TestValidator.equals("all analyzed_at match base timestamp")(
    timeFilter.data.every((r) => r.analyzed_at === baseTimestamp),
  )(true);

  // 2d. Search with filters yielding no matches
  const noMatch = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    { sentiment: "this-does-not-exist" },
  );
  typia.assert<IPageIReviewSentiment>(noMatch);
  TestValidator.equals("no-match yields empty data")(
    noMatch.data.length,
  )(0);
  TestValidator.equals("pagination still present")(
    typeof noMatch.pagination === "object",
  )(true);

  // 2e. Query with page far beyond total pages
  const outOfBounds = await api.functional.communicationReview.reviewSentiments.patch(
    connection,
    { page: 999 },
  );
  typia.assert<IPageIReviewSentiment>(outOfBounds);
  TestValidator.equals("out of bounds yields empty data")(
    outOfBounds.data.length,
  )(0);
  TestValidator.equals("page number reflects requested page")(
    outOfBounds.pagination.current,
  )(999);
}

