import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ISentimentAnalysisLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ISentimentAnalysisLog";
import typia from "typia";
import { ISentimentAnalysisLog as ISALNamespace } from "@ORGANIZATION/PROJECT-api/lib/structures/ISentimentAnalysisLog";
import { IPageISentimentAnalysisLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISentimentAnalysisLog";

export async function test_api_list_sentiment_analysis_logs_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // 1. Create 3 distinct sentiment analysis log entries
    const timeNowISO = new Date().toISOString();
    const createInputs: ISALNamespace.ICreate[] = [
        {
            review_id: "00000000-0000-0000-0000-000000000001",
            ai_model_version_id: "10000000-0000-0000-0000-000000000001",
            raw_input: "The pizza was great!",
            output: "positive",
            model_version: "v1.0.0-alpha",
            analyzed_at: timeNowISO,
        },
        {
            review_id: "00000000-0000-0000-0000-000000000002",
            ai_model_version_id: "10000000-0000-0000-0000-000000000002",
            raw_input: "The experience was neutral.",
            output: "neutral",
            model_version: "v1.1.0",
            analyzed_at: timeNowISO,
        },
        {
            review_id: "00000000-0000-0000-0000-000000000003",
            ai_model_version_id: "10000000-0000-0000-0000-000000000003",
            raw_input: "Service was terrible.",
            output: "negative",
            model_version: "v2.0.0",
            analyzed_at: timeNowISO,
        }
    ];
    const createdLogs: ISentimentAnalysisLog[] = [];
    for (const input of createInputs) {
        const log = await api.functional.communicationReview.sentimentAnalysisLogs.post(connection, input);
        typia.assert<ISentimentAnalysisLog>(log);
        createdLogs.push(log);
    }

    // 2. Filter for entry that should match exactly one (by review_id)
    const filterReq: ISALNamespace.IRequest = {
        review_id: createdLogs[0].review_id,
        // no other filters
    };
    const singleResult: IPageISentimentAnalysisLog = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, filterReq);
    typia.assert<IPageISentimentAnalysisLog>(singleResult);
    TestValidator.equals("single result count")([createdLogs[0]])(singleResult.data);
    TestValidator.predicate("pagination record matches")(() => singleResult.pagination.records === 1);

    // 3. Filter that leads to zero results (invalid/nonexistent review_id)
    const emptyReq: ISALNamespace.IRequest = {
        review_id: "99999999-9999-9999-9999-999999999999",
    };
    const emptyResult: IPageISentimentAnalysisLog = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, emptyReq);
    typia.assert<IPageISentimentAnalysisLog>(emptyResult);
    TestValidator.equals("zero data result")([] as ISentimentAnalysisLog[])(emptyResult.data);
    TestValidator.predicate("pagination zero check")(() => emptyResult.pagination.records === 0);

    // 4. Request a page that is definitely out of range (page > available)
    const overPageReq: ISALNamespace.IRequest = { page: 9999, limit: 2 };
    const overPageResult: IPageISentimentAnalysisLog = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, overPageReq);
    typia.assert<IPageISentimentAnalysisLog>(overPageResult);
    TestValidator.equals("over-page empty data")([] as ISentimentAnalysisLog[])(overPageResult.data);
    // pages should be at least 1, so pagination.pages <= 9999
    TestValidator.predicate("over-page pages check")(() => overPageResult.pagination.current === 9999);

    // 5. General broad filter (no filter), get all, default paging
    const broadReq: ISALNamespace.IRequest = {};
    const broadResult: IPageISentimentAnalysisLog = await api.functional.communicationReview.sentimentAnalysisLogs.patch(connection, broadReq);
    typia.assert<IPageISentimentAnalysisLog>(broadResult);
    // There should be at least the three created logs present
    TestValidator.predicate("broad result has >=3 logs")(() => broadResult.data.length >= 3);
    TestValidator.predicate("pagination total check")(() => broadResult.pagination.records >= 3);
}

