    // 4. Negative case: non-existent product_id
    const nonExistentProductInput: IFavoritesPersonalization.IFavoriteCreate = {
        user_id,
        product_id: "00000000-0000-4000-8000-FFFFFFFFFFFF", // not a real product id
    };
    await TestValidator.httpError("Should fail on non-existent product reference")(404, 400, 422)(
        () => api.functional.favoritesPersonalization.favorites.post(connection, nonExistentProductInput)
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";
import typia from "typia";

import { randomUUID } from "crypto";

export async function test_update_favorite_by_id_with_various_conditions(connection: api.IConnection): Promise<void> {
    // 1. Create favorite
    const createInput: IFavoritesPersonalization.IFavoriteCreate = {
        user_id: randomUUID(),
        product_id: randomUUID(),
    };
    const favorite = await api.functional.favoritesPersonalization.favorites.post(connection, createInput);
    typia.assert<IFavoritesPersonalization.IFavorite>(favorite);
    TestValidator.equals("user_id should match")(createInput.user_id)(favorite.user_id);
    TestValidator.equals("product_id should match")(createInput.product_id)(favorite.product_id);
    TestValidator.predicate("deleted_at is null")(() => favorite.deleted_at === null);

    // 2a. Update favorite (set deleted_at)
    const now = new Date().toISOString();
    const updatePayload: IFavoritesPersonalization.IFavoriteUpdate = { deleted_at: now };
    const updatedFavorite = await api.functional.favoritesPersonalization.favorites.put(connection, favorite.id, updatePayload);
    typia.assert<IFavoritesPersonalization.IFavorite>(updatedFavorite);
    TestValidator.equals("id should stay the same")(favorite.id)(updatedFavorite.id);
    TestValidator.equals("deleted_at updated")(now)(updatedFavorite.deleted_at);
    TestValidator.equals("user_id should not change")(favorite.user_id)(updatedFavorite.user_id);
    TestValidator.equals("product_id should not change")(favorite.product_id)(updatedFavorite.product_id);

    // 2b. Update non-existent favorite (should error)
    await TestValidator.httpError("update non-existent favorite should 404")(404)(
        async () => {
            await api.functional.favoritesPersonalization.favorites.put(
                connection,
                randomUUID(),
                { deleted_at: now },
            );
        }
    );

    // 2c. Update already deleted favorite (should error)
    await TestValidator.httpError("update already deleted favorite should 404")(404)(
        async () => {
            await api.functional.favoritesPersonalization.favorites.put(
                connection,
                favorite.id,
                { deleted_at: new Date(Date.now() + 1000).toISOString() },
            );
        }
    );

    // 2d. Attempt to update immutable fields (user_id, product_id)
    const illegalPayload: IFavoritesPersonalization.IFavoriteUpdate = { 
        // @ts-expect-error: These fields are forbidden, here for negative test
        user_id: randomUUID(),
        // @ts-expect-error: These fields are forbidden, here for negative test
        product_id: randomUUID(),
        deleted_at: now,
    };
    await TestValidator.httpError("immutable fields should not be updatable")(400, 422)(
        async () => {
            // Typescript will error if these fields are not allowed so we simulate improper usage by force-casting
            await api.functional.favoritesPersonalization.favorites.put(
                connection,
                favorite.id,
                illegalPayload as any,
            );
        }
    );
}

