import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

export async function test_api_list_bookmarks_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // Prepare test data: two users, three products, bookmarks with various optional metadata
    const USER1 = "11111111-1111-1111-1111-111111111111";
    const USER2 = "22222222-2222-2222-2222-222222222222";
    const PRODUCT_A = "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa";
    const PRODUCT_B = "bbbbbbbb-bbbb-bbbb-bbbb-bbbbbbbbbbbb";
    const PRODUCT_C = "cccccccc-cccc-cccc-cccc-cccccccccccc";

    // Bookmarks for USER1
    const bookmark1 = await api.functional.favoritesPersonalization.bookmarks.post(connection, {
        user_id: USER1,
        product_id: PRODUCT_A,
        custom_folder: "Work",
        tag: "urgent",
        note: "Finish ASAP"
    } as IFavoritesPersonalization.IBookmarkCreate);
    typia.assert<IFavoritesPersonalization.IBookmark>(bookmark1);

    const bookmark2 = await api.functional.favoritesPersonalization.bookmarks.post(connection, {
        user_id: USER1,
        product_id: PRODUCT_B,
        custom_folder: "Personal",
        tag: "review",
        note: "Review next week"
    } as IFavoritesPersonalization.IBookmarkCreate);
    typia.assert<IFavoritesPersonalization.IBookmark>(bookmark2);

    // Bookmark for USER2
    const bookmark3 = await api.functional.favoritesPersonalization.bookmarks.post(connection, {
        user_id: USER2,
        product_id: PRODUCT_A,
        custom_folder: null,
        tag: null,
        note: null
    } as IFavoritesPersonalization.IBookmarkCreate);
    typia.assert<IFavoritesPersonalization.IBookmark>(bookmark3);

    // Bookmark for USER1, PRODUCT_C (no optional fields)
    const bookmark4 = await api.functional.favoritesPersonalization.bookmarks.post(connection, {
        user_id: USER1,
        product_id: PRODUCT_C,
        custom_folder: null,
        tag: null,
        note: null
    } as IFavoritesPersonalization.IBookmarkCreate);
    typia.assert<IFavoritesPersonalization.IBookmark>(bookmark4);

    // 1. List all USER1 bookmarks (should get 3)
    const allUser1Req: IFavoritesPersonalization.IBookmarkSearchRequest = {
        user_id: USER1
    };
    const allUser1 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, allUser1Req);
    typia.assert<IFavoritesPersonalization.PageIBookmark>(allUser1);
    TestValidator.equals("all USER1 bookmarks -- count", () => false)([bookmark1, bookmark2, bookmark4].map(x => x.id))(
        allUser1.data.map(x => x.id));

    // 2. Filter by tag, folder and note
    const filterReq: IFavoritesPersonalization.IBookmarkSearchRequest = {
        user_id: USER1,
        tag: "urgent",
        custom_folder: "Work"
    };
    const filtered = await api.functional.favoritesPersonalization.bookmarks.patch(connection, filterReq);
    typia.assert<IFavoritesPersonalization.PageIBookmark>(filtered);
    TestValidator.equals("filtered bookmark1 only", () => false)([bookmark1.id])(
        filtered.data.map(x => x.id));
    if (filtered.data[0])
        TestValidator.equals("filtered tag/folder fields")("urgent")(filtered.data[0].tag);

    // 3. Pagination: limit = 2, page = 1
    const pagedReq: IFavoritesPersonalization.IBookmarkSearchRequest = {
        user_id: USER1,
        page: 1,
        limit: 2,
        sort: "created_at:asc"
    };
    const paged = await api.functional.favoritesPersonalization.bookmarks.patch(connection, pagedReq);
    typia.assert<IFavoritesPersonalization.PageIBookmark>(paged);
    TestValidator.equals("pagination page=1, limit=2")([bookmark1.id, bookmark2.id])(
        paged.data.map(x => x.id));
    TestValidator.equals("pagination metadata", () => false)({ current: 1, limit: 2 })(
        { current: paged.pagination.current, limit: paged.pagination.limit });

    // 4. Pagination: limit=2, page=2
    const paged2 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, { ...pagedReq, page: 2 });
    typia.assert<IFavoritesPersonalization.PageIBookmark>(paged2);
    TestValidator.equals("pagination page=2, remaining item")([bookmark4.id])(
        paged2.data.map(x => x.id));

    // 5. Edge: non-existent tag/folder (expect empty data)
    const emptyReq: IFavoritesPersonalization.IBookmarkSearchRequest = {
        user_id: USER1,
        tag: "nope",
        custom_folder: "doesnotexist"
    };
    const emptyResult = await api.functional.favoritesPersonalization.bookmarks.patch(connection, emptyReq);
    typia.assert<IFavoritesPersonalization.PageIBookmark>(emptyResult);
    TestValidator.equals("empty data edge", () => false)([] as string[])(emptyResult.data.map(x => x.id));

    // 6. Edge: invalid page number (out of bounds)
    const tooFarReq: IFavoritesPersonalization.IBookmarkSearchRequest = {
        user_id: USER1,
        page: 999,
        limit: 5
    };
    const tooFar = await api.functional.favoritesPersonalization.bookmarks.patch(connection, tooFarReq);
    typia.assert<IFavoritesPersonalization.PageIBookmark>(tooFar);
    TestValidator.equals("invalid page data empty", () => false)([] as string[])(tooFar.data.map(x => x.id));

    // 7. RBAC check: USER2 bookmarks listing
    const allUser2 = await api.functional.favoritesPersonalization.bookmarks.patch(connection, { user_id: USER2 });
    typia.assert<IFavoritesPersonalization.PageIBookmark>(allUser2);
    TestValidator.equals("User2 bookmarks", () => false)([bookmark3.id])(allUser2.data.map(x => x.id));
}

