import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";
import typia from "typia";

export async function test_api_create_bookmark_with_valid_and_invalid_data(connection: api.IConnection): Promise<void> {
    // 1. Prepare static test UUIDs
    const validUserId = "00000000-0000-4000-8000-000000000001";
    const validProductId = "00000000-0000-4000-8000-000000000002";
    const otherUserId = "00000000-0000-4000-8000-000000000003";
    const otherProductId = "00000000-0000-4000-8000-000000000004";
    const fakeUserId = "ffffffff-ffff-ffff-ffff-ffffffffffff";
    const fakeProductId = "eeeeeeee-eeee-eeee-eeee-eeeeeeeeeeee";

    // 2. Create valid bookmark
    const input: IFavoritesPersonalization.IBookmarkCreate = {
        user_id: validUserId,
        product_id: validProductId,
        custom_folder: "Wishlist",
        tag: "Summer2025",
        note: "Favorite pick for Seoul trip",
    };
    const created = await api.functional.favoritesPersonalization.bookmarks.post(connection, input);
    typia.assert<IFavoritesPersonalization.IBookmark>(created);
    // All required fields present, matching input where applicable
    TestValidator.equals("user_id")(input.user_id)(created.user_id);
    TestValidator.equals("product_id")(input.product_id)(created.product_id);
    TestValidator.equals("custom_folder")(input.custom_folder)(created.custom_folder);
    TestValidator.equals("tag")(input.tag)(created.tag);
    TestValidator.equals("note")(input.note)(created.note);
    TestValidator.predicate("created_at defined")(() => typeof created.created_at === "string" && created.created_at.length > 0);
    TestValidator.equals("deleted_at is null")(null)(created.deleted_at);

    // 3. Attempt creation with missing required fields
    await TestValidator.httpError("missing user_id")(400)(async () => {
        const badInput = { ...input };
        delete (badInput as any).user_id;
        return await api.functional.favoritesPersonalization.bookmarks.post(connection, badInput as any);
    });
    await TestValidator.httpError("missing product_id")(400)(async () => {
        const badInput = { ...input };
        delete (badInput as any).product_id;
        return await api.functional.favoritesPersonalization.bookmarks.post(connection, badInput as any);
    });
    await TestValidator.httpError("missing both user_id and product_id")(400)(async () => {
        const badInput: Partial<IFavoritesPersonalization.IBookmarkCreate> = { custom_folder: "Books" };
        return await api.functional.favoritesPersonalization.bookmarks.post(connection, badInput as any);
    });

    // 4. Uniqueness constraint: try to create duplicate bookmark (same user/product)
    await TestValidator.httpError("duplicate (user, product) bookmark")(409)(async () => {
        const dupe: IFavoritesPersonalization.IBookmarkCreate = {
            user_id: input.user_id,
            product_id: input.product_id,
            custom_folder: "New Folder",
            tag: null,
            note: null,
        };
        return await api.functional.favoritesPersonalization.bookmarks.post(connection, dupe);
    });

    // 5. But new bookmark is allowed for another product OR another user
    const input2: IFavoritesPersonalization.IBookmarkCreate = {
        user_id: validUserId,
        product_id: otherProductId,
        custom_folder: null,
        tag: null,
        note: null,
    };
    const created2 = await api.functional.favoritesPersonalization.bookmarks.post(connection, input2);
    typia.assert<IFavoritesPersonalization.IBookmark>(created2);
    TestValidator.equals("user_id")(input2.user_id)(created2.user_id);
    TestValidator.equals("product_id")(input2.product_id)(created2.product_id);

    const input3: IFavoritesPersonalization.IBookmarkCreate = {
        user_id: otherUserId,
        product_id: validProductId,
        custom_folder: null,
        tag: "Book", // allow only tag
        note: null,
    };
    const created3 = await api.functional.favoritesPersonalization.bookmarks.post(connection, input3);
    typia.assert<IFavoritesPersonalization.IBookmark>(created3);
    TestValidator.equals("user_id")(input3.user_id)(created3.user_id);
    TestValidator.equals("product_id")(input3.product_id)(created3.product_id);
    TestValidator.equals("tag")(input3.tag)(created3.tag);

    // 6. Foreign key: invalid user_id or product_id
    await TestValidator.httpError("invalid user_id")(404,400)(async () => {
        const badInput: IFavoritesPersonalization.IBookmarkCreate = {
            user_id: fakeUserId,
            product_id: validProductId,
            custom_folder: null,
            tag: null,
            note: null,
        };
        return await api.functional.favoritesPersonalization.bookmarks.post(connection, badInput);
    });
    await TestValidator.httpError("invalid product_id")(404,400)(async () => {
        const badInput: IFavoritesPersonalization.IBookmarkCreate = {
            user_id: validUserId,
            product_id: fakeProductId,
            custom_folder: null,
            tag: null,
            note: null,
        };
        return await api.functional.favoritesPersonalization.bookmarks.post(connection, badInput);
    });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

import { v4 as uuidv4 } from "uuid";

export async function test_api_update_bookmark_with_various_conditions(connection: api.IConnection): Promise<void> {
    // 1. Create a bookmark (for update and negative scenarios)
    const createInput: IFavoritesPersonalization.IBookmarkCreate = {
        user_id: uuidv4(),
        product_id: uuidv4(),
        custom_folder: "Initial Folder",
        tag: "init-tag",
        note: "Initial note"
    };
    const created = await api.functional.favoritesPersonalization.bookmarks.post(connection, createInput);
    typia.assert<IFavoritesPersonalization.IBookmark>(created);

    // 2. Update mutable fields (custom_folder, tag, note)
    const updateInput: IFavoritesPersonalization.IBookmarkUpdate = {
        custom_folder: "Updated Folder",
        tag: "updated-tag",
        note: "Updated note"
    };
    const updated = await api.functional.favoritesPersonalization.bookmarks.put(connection, created.id, updateInput);
    typia.assert<IFavoritesPersonalization.IBookmark>(updated);
    TestValidator.equals("updated id")(created.id)(updated.id);
    TestValidator.equals("user_id immutable")(created.user_id)(updated.user_id);
    TestValidator.equals("product_id immutable")(created.product_id)(updated.product_id);
    TestValidator.equals("created_at unchanged")(created.created_at)(updated.created_at);
    TestValidator.equals("custom_folder updated")(updateInput.custom_folder)(updated.custom_folder);
    TestValidator.equals("tag updated")(updateInput.tag)(updated.tag);
    TestValidator.equals("note updated")(updateInput.note)(updated.note);
    TestValidator.equals("deleted_at null")(null)(updated.deleted_at ?? null);

    // 3. Not Found: Try to update a non-existent bookmark (random UUID)
    const nonExistentId = uuidv4();
    await TestValidator.httpError("update non-existent bookmark")(404)(async () => {
        await api.functional.favoritesPersonalization.bookmarks.put(connection, nonExistentId, updateInput);
    });

    // 4. Simulate deletion, then attempt another update (expect error)
    const softDeleteInput: IFavoritesPersonalization.IBookmarkUpdate = {
        deleted_at: new Date().toISOString()
    };
    const deletedBookmark = await api.functional.favoritesPersonalization.bookmarks.put(connection, created.id, softDeleteInput);
    typia.assert<IFavoritesPersonalization.IBookmark>(deletedBookmark);
    TestValidator.equals("deleted_at set")(softDeleteInput.deleted_at)(deletedBookmark.deleted_at);
    // Attempt update after deletion
    await TestValidator.httpError("update deleted bookmark")(404)(async () => {
        await api.functional.favoritesPersonalization.bookmarks.put(connection, created.id, updateInput);
    });

    // 5. Try to mutate immutable fields (should be rejected or ignored)
    const illegalUpdate = {
        ...updateInput,
        user_id: uuidv4(),
        product_id: uuidv4(),
        id: uuidv4(),
        created_at: new Date().toISOString(),
    } as any;
    await TestValidator.httpError("illegal update fields should fail or be ignored")(400, 422)(async () => {
        await api.functional.favoritesPersonalization.bookmarks.put(connection, created.id, illegalUpdate);
    });
}

